import { tokenService } from '../services/tokenService';

/**
 * A utility hook that prevents authenticated API calls for guest users
 * Returns null data and false loading state for guest users
 */
export function useAuthenticatedData<T>(
  hookFn: () => { data: T; loading: boolean; error: any },
  defaultValue: T
): { data: T; loading: boolean; error: any } {
  const isLoggedIn = tokenService.isLoggedIn();

  if (!isLoggedIn) {
    return {
      data: defaultValue,
      loading: false,
      error: null
    };
  }

  return hookFn();
}
