import { Alert, Button, Card, CardActions, CardContent, CardMedia, CircularProgress, Container, Typography } from '@mui/material';
import React, { useEffect, useState } from 'react';
import type { BorrowedBookResponse } from '../app/services/models/BorrowedBookResponse';
import { BookService } from '../app/services/services/BookService';

const BorrowedBooksPage: React.FC = () => {
  const [books, setBooks] = useState<BorrowedBookResponse[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<any>(null);

  useEffect(() => {
    loadBorrowedBooks();
  }, []);

  const loadBorrowedBooks = () => {
    BookService.findAllBorrowedBooks()
      .then(res => setBooks(res.content || []))
      .catch(setError)
      .finally(() => setLoading(false));
  };

  const handleMarkAsRead = async (bookId: number) => {
    try {
      await BookService.markBookAsRead(bookId);
      // After marking as read, remove from borrowed books
      setBooks(books.filter(book => book.id !== bookId));
    } catch (error) {
      console.error('Error marking book as read:', error);
    }
  };

  const handleReturnBook = async (bookId: number) => {
    try {
      console.log('Returning book:', bookId);
      await BookService.returnBorrowBook(bookId);
      console.log('Book returned successfully');
      // Remove from borrowed books after returning
      setBooks(books.filter(book => book.id !== bookId));
    } catch (error) {
      console.error('Error returning book:', error);
      setError(error);
    }
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4 }}>
      <Typography variant="h4" gutterBottom>Borrowed Books</Typography>
      {loading && <CircularProgress />}
      {error && <Alert severity="error">{error.message || String(error)}</Alert>}

      {books.length === 0 && !loading && (
        <Typography variant="body1" color="text.secondary">
          You haven't borrowed any books yet.
        </Typography>
      )}

      <div className="book-list-grid">
        {books.map(book => (
          <div className="book-list-card" key={book.id}>
            <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column', maxWidth: 200 }}>
              {book.cover ? (
                <CardMedia
                  component="img"
                  height="120"
                  image={`data:image/jpeg;base64,${book.cover}`}
                  alt={book.title}
                  sx={{ objectFit: 'contain' }}
                />
              ) : (
                <CardMedia
                  component="div"
                  sx={{ height: 120, background: '#eee', display: 'flex', alignItems: 'center', justifyContent: 'center' }}
                >
                  <Typography variant="body2" color="text.secondary">
                    No Cover
                  </Typography>
                </CardMedia>
              )}
              <CardContent sx={{ flexGrow: 1, p: 1.5 }}>
                <Typography variant="subtitle1" component="div" sx={{ fontWeight: 'bold', mb: 0.5, lineHeight: 1.2 }}>
                  {book.title}
                </Typography>
                <Typography variant="body2" color="text.secondary" sx={{ mb: 1 }}>
                  by {book.authorName}
                </Typography>
              </CardContent>
              <CardActions sx={{ p: 1.5, pt: 0 }}>
                <Button 
                  size="small" 
                  variant="outlined" 
                  color="primary"
                  onClick={() => book.id && handleMarkAsRead(book.id)}
                  sx={{ flex: 1, mr: 1 }}
                >
                  Mark as Read
                </Button>
                <Button 
                  size="small" 
                  variant="outlined" 
                  color="secondary"
                  onClick={() => book.id && handleReturnBook(book.id)}
                  sx={{ flex: 1 }}
                >
                  Return
                </Button>
              </CardActions>
            </Card>
          </div>
        ))}
      </div>
    </Container>
  );
};

export default BorrowedBooksPage;
