import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import {
  Edit as EditIcon,
  LocationOn as LocationIcon,
  Language as WebsiteIcon,
  Twitter as TwitterIcon,
  Instagram as InstagramIcon,
  Book as BookIcon,
  Person as PersonIcon,
  CalendarToday as CalendarIcon,
  Star as StarIcon,
  RateReview as ReviewIcon,
  People as PeopleIcon,
  Add as FollowIcon,
  Check as FollowingIcon
} from '@mui/icons-material';
import { UserProfileService, UserProfileResponse } from '../app/services/services/UserProfileService';
import { tokenService } from '../services/tokenService';

type UserProfile = UserProfileResponse;

const ProfilePage: React.FC = () => {
  const { userId } = useParams<{ userId: string }>();
  const navigate = useNavigate();
  const [profile, setProfile] = useState<UserProfile | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [activeTab, setActiveTab] = useState(0);
  const [followLoading, setFollowLoading] = useState(false);

  useEffect(() => {
    if (!userId) return;

    const fetchProfile = async () => {
      try {
        setLoading(true);
        const profileData = await UserProfileService.getUserProfile(parseInt(userId));
        setProfile(profileData);
      } catch (err: any) {
        setError(err?.body?.message || 'Failed to load profile');
      } finally {
        setLoading(false);
      }
    };

    fetchProfile();
  }, [userId]);

  const handleFollow = async () => {
    if (!profile || !userId) return;

    try {
      setFollowLoading(true);
      if (profile.isFollowing) {
        await UserProfileService.unfollowUser(parseInt(userId));
        setProfile(prev => prev ? { ...prev, isFollowing: false, followersCount: prev.followersCount - 1 } : null);
      } else {
        await UserProfileService.followUser(parseInt(userId));
        setProfile(prev => prev ? { ...prev, isFollowing: true, followersCount: prev.followersCount + 1 } : null);
      }
    } catch (err: any) {
      setError(err?.body?.message || 'Failed to update follow status');
    } finally {
      setFollowLoading(false);
    }
  };

  const handleEditProfile = () => {
    navigate(`/profile/${userId}/edit`);
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  if (loading) {
    return (
      <div style={{ 
        minHeight: '100vh', 
        backgroundColor: '#FAF3E3',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center'
      }}>
        <div style={{
          width: '40px',
          height: '40px',
          border: '4px solid #E6D7C3',
          borderTop: '4px solid #D2691E',
          borderRadius: '50%',
          animation: 'spin 1s linear infinite'
        }}></div>
        <style>{`
          @keyframes spin {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
          }
        `}</style>
      </div>
    );
  }

  if (error) {
    return (
      <div style={{ 
        minHeight: '100vh', 
        backgroundColor: '#FAF3E3',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center'
      }}>
        <div style={{
          backgroundColor: 'rgba(255, 255, 255, 0.9)',
          padding: '24px',
          borderRadius: '12px',
          border: '1px solid #E6D7C3',
          textAlign: 'center'
        }}>
          <h3 style={{
            fontFamily: 'Playfair Display, serif',
            color: '#4B3F30',
            marginBottom: '8px'
          }}>
            Error Loading Profile
          </h3>
          <p style={{ color: '#6A5E4D' }}>
            {error}
          </p>
        </div>
      </div>
    );
  }

  if (!profile) {
    return (
      <div style={{ 
        minHeight: '100vh', 
        backgroundColor: '#FAF3E3',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center'
      }}>
        <div style={{
          backgroundColor: 'rgba(255, 255, 255, 0.9)',
          padding: '24px',
          borderRadius: '12px',
          border: '1px solid #E6D7C3',
          textAlign: 'center'
        }}>
          <h3 style={{
            fontFamily: 'Playfair Display, serif',
            color: '#4B3F30',
            marginBottom: '8px'
          }}>
            Profile Not Found
          </h3>
          <p style={{ color: '#6A5E4D' }}>
            The profile you're looking for doesn't exist.
          </p>
        </div>
      </div>
    );
  }

  return (
    <div style={{ minHeight: '100vh', backgroundColor: '#FAF3E3' }}>
      {/* Header Section */}
      <div style={{
        background: 'linear-gradient(90deg, #4B3F30, #5D4A33, #4B3F30)',
        color: 'white',
        padding: '48px 0',
        textAlign: 'center'
      }}>
        <div style={{ maxWidth: '1200px', margin: '0 auto', padding: '0 24px' }}>
          <h1 style={{
            fontFamily: 'Playfair Display, serif',
            fontSize: '36px',
            fontWeight: 700,
            marginBottom: '16px'
          }}>
            Profile
          </h1>
          <p style={{
            fontSize: '18px',
            color: 'rgba(255, 255, 255, 0.9)',
            maxWidth: '600px',
            margin: '0 auto'
          }}>
            Discover the literary world of {profile.displayName || profile.fullName}
          </p>
        </div>
      </div>

      <div style={{ maxWidth: '1200px', margin: '0 auto', padding: '48px 24px' }}>
        {/* Profile Header Card */}
        <div style={{
          backgroundColor: 'rgba(255, 255, 255, 0.8)',
          backdropFilter: 'blur(10px)',
          padding: '32px',
          borderRadius: '16px',
          border: '1px solid #E6D7C3',
          boxShadow: '0 4px 20px rgba(75, 63, 48, 0.1)',
          marginBottom: '32px'
        }}>
          <div style={{
            display: 'grid',
            gridTemplateColumns: '1fr 2fr',
            gap: '32px',
            alignItems: 'start'
          }}>
            {/* Avatar and Basic Info */}
            <div style={{ textAlign: 'center' }}>
              <div style={{
                width: '120px',
                height: '120px',
                borderRadius: '50%',
                backgroundColor: '#E6D7C3',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                margin: '0 auto 16px',
                fontSize: '48px',
                color: '#8B7355',
                fontWeight: 'bold'
              }}>
                {profile.displayName?.charAt(0) || profile.fullName?.charAt(0) || 'U'}
              </div>
              <h2 style={{
                fontFamily: 'Playfair Display, serif',
                fontSize: '28px',
                fontWeight: 600,
                color: '#4B3F30',
                marginBottom: '8px'
              }}>
                {profile.displayName || profile.fullName}
              </h2>
              <p style={{
                color: '#6A5E4D',
                fontSize: '16px',
                marginBottom: '16px'
              }}>
                @{profile.username}
              </p>

              {/* Follow Button */}
              {!profile.isOwnProfile && (
                <button
                  style={{
                    backgroundColor: profile.isFollowing ? 'transparent' : '#D2691E',
                    color: profile.isFollowing ? '#D2691E' : 'white',
                    border: `1px solid #D2691E`,
                    borderRadius: '8px',
                    padding: '12px 24px',
                    fontSize: '16px',
                    fontWeight: 500,
                    cursor: 'pointer',
                    transition: 'all 0.2s',
                    display: 'flex',
                    alignItems: 'center',
                    gap: '8px',
                    margin: '0 auto'
                  }}
                  onMouseOver={(e) => {
                    if (profile.isFollowing) {
                      e.currentTarget.style.backgroundColor = '#D2691E';
                      e.currentTarget.style.color = 'white';
                    } else {
                      e.currentTarget.style.backgroundColor = '#B85A1A';
                    }
                  }}
                  onMouseOut={(e) => {
                    if (profile.isFollowing) {
                      e.currentTarget.style.backgroundColor = 'transparent';
                      e.currentTarget.style.color = '#D2691E';
                    } else {
                      e.currentTarget.style.backgroundColor = '#D2691E';
                    }
                  }}
                  onClick={handleFollow}
                  disabled={followLoading}
                >
                  {profile.isFollowing ? <FollowingIcon /> : <FollowIcon />}
                  {profile.isFollowing ? 'Following' : 'Follow'}
                </button>
              )}

              {/* Edit Profile Button */}
              {profile.isOwnProfile && (
                <button
                  style={{
                    backgroundColor: 'transparent',
                    color: '#D2691E',
                    border: '1px solid #D2691E',
                    borderRadius: '8px',
                    padding: '12px 24px',
                    fontSize: '16px',
                    fontWeight: 500,
                    cursor: 'pointer',
                    transition: 'all 0.2s',
                    display: 'flex',
                    alignItems: 'center',
                    gap: '8px',
                    margin: '0 auto'
                  }}
                  onMouseOver={(e) => {
                    e.currentTarget.style.backgroundColor = '#D2691E';
                    e.currentTarget.style.color = 'white';
                  }}
                  onMouseOut={(e) => {
                    e.currentTarget.style.backgroundColor = 'transparent';
                    e.currentTarget.style.color = '#D2691E';
                  }}
                  onClick={handleEditProfile}
                >
                  <EditIcon />
                  Edit Profile
                </button>
              )}
            </div>

            {/* Profile Details */}
            <div>
              {profile.bio && (
                <p style={{
                  fontSize: '18px',
                  color: '#4B3F30',
                  marginBottom: '24px',
                  lineHeight: 1.6
                }}>
                  {profile.bio}
                </p>
              )}

              {/* Stats */}
              <div style={{
                display: 'grid',
                gridTemplateColumns: 'repeat(auto-fit, minmax(120px, 1fr))',
                gap: '16px',
                marginBottom: '24px'
              }}>
                <div style={{
                  textAlign: 'center',
                  padding: '16px',
                  backgroundColor: 'rgba(255, 255, 255, 0.5)',
                  borderRadius: '8px',
                  border: '1px solid #E6D7C3'
                }}>
                  <div style={{
                    fontSize: '24px',
                    fontWeight: 'bold',
                    color: '#D2691E',
                    marginBottom: '4px'
                  }}>
                    {profile.followersCount || 0}
                  </div>
                  <div style={{
                    fontSize: '14px',
                    color: '#6A5E4D'
                  }}>
                    Followers
                  </div>
                </div>
                <div style={{
                  textAlign: 'center',
                  padding: '16px',
                  backgroundColor: 'rgba(255, 255, 255, 0.5)',
                  borderRadius: '8px',
                  border: '1px solid #E6D7C3'
                }}>
                  <div style={{
                    fontSize: '24px',
                    fontWeight: 'bold',
                    color: '#D2691E',
                    marginBottom: '4px'
                  }}>
                    {profile.followingCount || 0}
                  </div>
                  <div style={{
                    fontSize: '14px',
                    color: '#6A5E4D'
                  }}>
                    Following
                  </div>
                </div>
              </div>

              {/* Contact Info */}
              {(profile.location || profile.website) && (
                <div style={{ marginBottom: '16px' }}>
                  {profile.location && (
                    <div style={{
                      display: 'inline-flex',
                      alignItems: 'center',
                      gap: '8px',
                      padding: '8px 12px',
                      backgroundColor: 'rgba(255, 255, 255, 0.5)',
                      borderRadius: '6px',
                      border: '1px solid #E6D7C3',
                      marginRight: '8px',
                      marginBottom: '8px'
                    }}>
                      <LocationIcon style={{ color: '#8B7355', fontSize: '16px' }} />
                      <span style={{ color: '#4B3F30', fontSize: '14px' }}>
                        {profile.location}
                      </span>
                    </div>
                  )}
                  {profile.website && (
                    <button
                      style={{
                        display: 'inline-flex',
                        alignItems: 'center',
                        gap: '8px',
                        padding: '8px 12px',
                        backgroundColor: 'rgba(255, 255, 255, 0.5)',
                        borderRadius: '6px',
                        border: '1px solid #E6D7C3',
                        marginRight: '8px',
                        marginBottom: '8px',
                        cursor: 'pointer',
                        transition: 'all 0.2s'
                      }}
                      onMouseOver={(e) => {
                        e.currentTarget.style.backgroundColor = 'rgba(255, 255, 255, 0.8)';
                        e.currentTarget.style.borderColor = '#D2691E';
                      }}
                      onMouseOut={(e) => {
                        e.currentTarget.style.backgroundColor = 'rgba(255, 255, 255, 0.5)';
                        e.currentTarget.style.borderColor = '#E6D7C3';
                      }}
                      onClick={() => profile.website && window.open(profile.website, '_blank')}
                    >
                      <WebsiteIcon style={{ color: '#8B7355', fontSize: '16px' }} />
                      <span style={{ color: '#4B3F30', fontSize: '14px' }}>
                        Website
                      </span>
                    </button>
                  )}
                </div>
              )}

              {/* Join Date */}
              {profile.createdAt && (
                <div style={{
                  display: 'flex',
                  alignItems: 'center',
                  gap: '8px',
                  color: '#6A5E4D',
                  fontSize: '14px'
                }}>
                  <CalendarIcon style={{ fontSize: '16px' }} />
                  <span>Joined {formatDate(profile.createdAt)}</span>
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Placeholder for future content (books, reviews, etc.) */}
        <div style={{
          backgroundColor: 'rgba(255, 255, 255, 0.8)',
          backdropFilter: 'blur(10px)',
          padding: '32px',
          borderRadius: '16px',
          border: '1px solid #E6D7C3',
          boxShadow: '0 4px 20px rgba(75, 63, 48, 0.1)',
          textAlign: 'center'
        }}>
          <BookIcon style={{ 
            color: '#8B7355', 
            fontSize: '48px',
            marginBottom: '16px'
          }} />
          <h3 style={{
            fontFamily: 'Playfair Display, serif',
            fontSize: '24px',
            color: '#4B3F30',
            marginBottom: '8px'
          }}>
            More Content Coming Soon
          </h3>
          <p style={{
            color: '#6A5E4D',
            fontSize: '16px'
          }}>
            Books, reviews, and reading activity will be displayed here.
          </p>
        </div>
      </div>
    </div>
  );
};

export default ProfilePage;
