import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  CircularProgress,
  Alert
} from '@mui/material';
import {
  TrendingUp,
  Coffee,
  AutoStories,
  Favorite,
  Star,
  MenuBook
} from '@mui/icons-material';
import PaginationControls from '../components/PaginationControls';
import { useBooks } from '../hooks/useBooks';
import { useBorrowedBooks } from '../hooks/useBorrowedBooks';
import { useMyBooks } from '../hooks/useMyBooks';
import { useReadBooks } from '../hooks/useReadBooks';

const BookListPage: React.FC = () => {
  const { data: allBooks, loading: allBooksLoading, error: allBooksError, page, setPage, size, setSize } = useBooks();
  const { data: borrowedBooks, loading: borrowedLoading } = useBorrowedBooks();
  const { data: myBooks, loading: myBooksLoading } = useMyBooks();
  const { data: readBooks, loading: readBooksLoading } = useReadBooks();
  const navigate = useNavigate();
  const [searchQuery] = useState('');

  // Get currently reading book (first borrowed book)
  const currentlyReading = borrowedBooks?.content?.[0];

  // Get trending books (first 5 from all books)
  const trendingBooks = allBooks?.content?.slice(0, 5) || [];

  // Get to-read books (my books that aren't borrowed or read)
  const toReadBooks = myBooks?.content?.filter(book => 
    !borrowedBooks?.content?.some(borrowed => borrowed.id === book.id) &&
    !readBooks?.content?.some(read => read.id === book.id)
  ).slice(0, 4) || [];

  // Get favorite books (read books, first 2)
  const favoriteBooks = readBooks?.content?.slice(0, 2) || [];

  const renderBookCover = (book: any, className: string = "") => {
    if (book.cover) {
      return (
        <img
          src={typeof book.cover === 'string' && book.cover.startsWith('http') ? 
               `http://localhost:8088/api/v1/books/cover/${book.id}` : 
               `data:image/jpeg;base64,${book.cover}`}
          alt={book.title}
          className={`object-cover rounded-lg ${className}`}
        />
      );
    }
    return (
      <div className={`bg-gradient-to-br from-vintage-beige to-vintage-tan flex items-center justify-center rounded-lg ${className}`}>
        <MenuBook className="text-vintage-brown-600 text-2xl" />
      </div>
    );
  };

  const EmptyState = ({ icon: Icon, title, description }: { icon: any, title: string, description: string }) => (
    <div className="vintage-card text-center py-8">
      <Icon className="text-vintage-brown-400 text-4xl mb-4 mx-auto" />
      <h4 className="font-playfair text-lg font-semibold text-vintage-brown-700 mb-2">{title}</h4>
      <p className="text-vintage-brown-500 text-sm">{description}</p>
    </div>
  );

  return (
    <div className="min-h-screen bg-gradient-to-br from-vintage-cream via-vintage-paper to-vintage-beige vintage-pattern">
      {/* Decorative Header */}
      <div className="relative overflow-hidden bg-gradient-to-r from-vintage-brown-900 via-vintage-brown-800 to-vintage-brown-900 text-white py-12">
        <div className="absolute inset-0 opacity-20">
          <div className="absolute inset-0 bg-gradient-to-r from-transparent via-white/5 to-transparent"></div>
        </div>
        <div className="max-w-7xl mx-auto px-6 relative">
          <div className="text-center">
            <h1 className="font-playfair text-4xl md:text-5xl font-bold mb-4">
              Welcome to Your Literary Haven
            </h1>
            <p className="text-xl text-vintage-cream/90 max-w-2xl mx-auto">
              Discover, track, and share your reading journey in our cozy digital bookstore
            </p>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
        {/* 3-Column Layout */}
        <div className="grid grid-cols-1 lg:grid-cols-12 gap-8 mb-12">

          {/* LEFT COLUMN — Currently Reading */}
          <div className="lg:col-span-4">
            <div className="flex items-center mb-6">
              <Coffee className="text-vintage-brown-600 mr-3 text-2xl" />
              <h2 className="section-title mb-0">Currently Reading</h2>
            </div>

            {currentlyReading ? (
              <div className="vintage-card">
                <div className="flex gap-4">
                  {renderBookCover(currentlyReading, "w-24 h-32 flex-shrink-0")}
                  <div className="flex-1">
                    <h3 className="font-playfair text-lg font-semibold text-vintage-brown-800 mb-2 leading-tight">
                      {currentlyReading.title}
                    </h3>
                    <p className="text-vintage-brown-600 mb-4 font-medium">
                      by {currentlyReading.authorName}
                    </p>

                    {/* Progress Bar */}
                    <div className="mb-4">
                      <div className="flex justify-between mb-2">
                        <span className="text-vintage-brown-600 text-sm font-medium">Progress</span>
                        <span className="text-vintage-brown-800 text-sm font-semibold">0%</span>
                      </div>
                      <div className="w-full bg-vintage-tan rounded-full h-2.5">
                        <div className="bg-gradient-to-r from-vintage-amber-500 to-vintage-amber-600 h-2.5 rounded-full transition-all duration-300" style={{ width: '0%' }}></div>
                      </div>
                    </div>

                    <button className="btn-primary w-full">
                      Update Progress
                    </button>
                  </div>
                </div>
              </div>
            ) : (
              <EmptyState 
                icon={AutoStories}
                title="Start Your Reading Journey"
                description={borrowedLoading ? 'Loading your books...' : 'Borrow your first book to begin reading!'}
              />
            )}
          </div>

          {/* CENTER COLUMN — Trending Today */}
          <div className="lg:col-span-5">
            <div className="flex items-center mb-6">
              <TrendingUp className="text-vintage-brown-600 mr-3 text-2xl" />
              <h2 className="section-title mb-0">Trending Today</h2>
            </div>

            {allBooksLoading ? (
              <div className="vintage-card flex justify-center py-12">
                <CircularProgress sx={{ color: '#B8956A' }} />
              </div>
            ) : trendingBooks.length > 0 ? (
              <div className="vintage-card">
                <div className="flex gap-4 overflow-x-auto scrollbar-hidden pb-2">
                  {trendingBooks.map((book) => (
                    <div 
                      key={book.id}
                      className="book-card min-w-[140px] max-w-[140px] flex-shrink-0"
                      onClick={() => navigate(`/books/${book.id}`)}
                    >
                      {renderBookCover(book, "w-full h-44 mb-3")}
                      <h4 className="font-playfair text-sm font-semibold text-vintage-brown-800 mb-1 leading-tight line-clamp-2">
                        {book.title}
                      </h4>
                      <p className="text-vintage-brown-600 text-xs font-medium truncate">
                        {book.authorName}
                      </p>
                    </div>
                  ))}
                </div>
              </div>
            ) : (
              <EmptyState 
                icon={TrendingUp}
                title="No Trending Books"
                description="Check back later for popular reads!"
              />
            )}

            {/* From Friends Section */}
            <div className="mt-8">
              <h2 className="section-title">From Friends</h2>
              <EmptyState 
                icon={Favorite}
                title="Connect with Fellow Readers"
                description="Social features coming soon! Connect with friends to see their book reviews and recommendations."
              />
            </div>
          </div>

          {/* RIGHT COLUMN — To Read & Favorites */}
          <div className="lg:col-span-3">
            {/* To Read Section */}
            <div className="mb-8">
              <h2 className="section-title">Want to Read</h2>
              {myBooksLoading ? (
                <div className="vintage-card flex justify-center py-8">
                  <CircularProgress sx={{ color: '#B8956A' }} size={32} />
                </div>
              ) : toReadBooks.length > 0 ? (
                <div className="vintage-card">
                  <div className="grid grid-cols-2 gap-3">
                    {toReadBooks.map((book) => (
                      <div 
                        key={book.id}
                        className="book-card p-2"
                        onClick={() => navigate(`/books/${book.id}`)}
                      >
                        {renderBookCover(book, "w-full h-24")}
                      </div>
                    ))}
                  </div>
                </div>
              ) : (
                <EmptyState 
                  icon={AutoStories}
                  title="Build Your Reading List"
                  description="Add books you want to read to your list!"
                />
              )}
            </div>

            {/* Favorites Section */}
            <div>
              <div className="flex items-center mb-6">
                <Star className="text-vintage-amber-500 mr-2 text-xl" />
                <h2 className="section-title mb-0">Favorites</h2>
              </div>
              {readBooksLoading ? (
                <div className="vintage-card flex justify-center py-8">
                  <CircularProgress sx={{ color: '#B8956A' }} size={32} />
                </div>
              ) : favoriteBooks.length > 0 ? (
                <div className="vintage-card">
                  <div className="grid grid-cols-2 gap-3">
                    {favoriteBooks.map((book) => (
                      <div 
                        key={book.id}
                        className="book-card p-2"
                        onClick={() => navigate(`/books/${book.id}`)}
                      >
                        {renderBookCover(book, "w-full h-24")}
                      </div>
                    ))}
                  </div>
                </div>
              ) : (
                <EmptyState 
                  icon={Favorite}
                  title="No Favorites Yet"
                  description="Mark books as favorites when you finish reading them!"
                />
              )}
            </div>
          </div>
        </div>

        {/* All Books Section */}
        {searchQuery && (
          <div className="mb-8">
            <h2 className="section-title">Search Results</h2>
          </div>
        )}

        {allBooksLoading && (
          <div className="flex justify-center py-12">
            <CircularProgress sx={{ color: '#B8956A' }} size={48} />
          </div>
        )}

        {allBooksError && (
          <Alert severity="error" className="mb-8">
            {allBooksError.message || String(allBooksError)}
          </Alert>
        )}

        {allBooks?.content && allBooks.content.length > 0 && (
          <div>
            <h2 className="section-title">Discover Books</h2>

            <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 xl:grid-cols-6 gap-6">
              {allBooks.content.map(book => (
                <div key={book.id}>
                  <div 
                    className="book-card h-full"
                    onClick={() => navigate(`/books/${book.id}`)}
                  >
                    {renderBookCover(book, "w-full h-48 mb-4")}
                    <div>
                      <h4 className="font-playfair text-sm font-semibold text-vintage-brown-800 mb-2 leading-tight line-clamp-2">
                        {book.title}
                      </h4>
                      <p className="text-vintage-brown-600 mb-3 text-xs font-medium line-clamp-1">
                        by {book.authorName}
                      </p>
                      <button 
                        className="btn-secondary w-full text-xs py-1.5"
                        onClick={(e) => {
                          e.stopPropagation();
                          navigate(`/books/${book.id}`);
                        }}
                      >
                        View Details
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>

            {allBooks && (
              <div className="mt-12">
                <PaginationControls
                  currentPage={page}
                  totalPages={allBooks.totalPages || 0}
                  pageSize={size}
                  totalElements={allBooks.totalElements || 0}
                  onPageChange={setPage}
                  onPageSizeChange={setSize}
                  loading={allBooksLoading}
                />
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default BookListPage;
