import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  CircularProgress,
  Alert,
  LinearProgress
} from '@mui/material';
import {
  TrendingUp,
  Coffee,
  Lightbulb
} from '@mui/icons-material';
import PaginationControls from '../components/PaginationControls';
import { useBooks } from '../hooks/useBooks';
import { useBorrowedBooks } from '../hooks/useBorrowedBooks';
import { useMyBooks } from '../hooks/useMyBooks';
import { useReadBooks } from '../hooks/useReadBooks';

const BookListPage: React.FC = () => {
  const { data: allBooks, loading: allBooksLoading, error: allBooksError, page, setPage, size, setSize } = useBooks();
  const { data: borrowedBooks, loading: borrowedLoading } = useBorrowedBooks();
  const { data: myBooks, loading: myBooksLoading } = useMyBooks();
  const { data: readBooks, loading: readBooksLoading } = useReadBooks();
  const navigate = useNavigate();
  const [searchQuery] = useState('');

  // Get currently reading book (first borrowed book)
  const currentlyReading = borrowedBooks?.content?.[0];

  // Get trending books (first 5 from all books)
  const trendingBooks = allBooks?.content?.slice(0, 5) || [];

  // Get to-read books (my books that aren't borrowed or read)
  const toReadBooks = myBooks?.content?.filter(book => 
    !borrowedBooks?.content?.some(borrowed => borrowed.id === book.id) &&
    !readBooks?.content?.some(read => read.id === book.id)
  ).slice(0, 4) || [];

  // Get favorite books (read books, first 2)
  const favoriteBooks = readBooks?.content?.slice(0, 2) || [];

  return (
    <div className="min-h-screen bg-[#FAF3E3] py-6 px-6 relative">
      {/* Background Pattern */}
      <div 
        className="absolute inset-0 opacity-30 pointer-events-none"
        style={{
          backgroundImage: `url("data:image/svg+xml,%3Csvg width='100' height='100' xmlns='http://www.w3.org/2000/svg'%3E%3Cdefs%3E%3Cpattern id='bookshelf' x='0' y='0' width='100' height='100' patternUnits='userSpaceOnUse'%3E%3Crect width='100' height='100' fill='none'/%3E%3Crect x='10' y='20' width='80' height='4' fill='%23E6D7C3' opacity='0.1'/%3E%3Crect x='10' y='40' width='80' height='4' fill='%23E6D7C3' opacity='0.1'/%3E%3Crect x='10' y='60' width='80' height='4' fill='%23E6D7C3' opacity='0.1'/%3E%3C/pattern%3E%3C/defs%3E%3Crect width='100%25' height='100%25' fill='url(%23bookshelf)'/%3E%3C/svg%3E")`
        }}
      />

      <div className="max-w-7xl mx-auto relative z-10">
        {/* 3-Column Layout */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-6">

          {/* LEFT COLUMN — Currently Reading */}
          <div className="md:col-span-1">
            <h3 className="text-xl font-semibold font-playfair text-[#4B3F30] mb-6">
              Currently Reading
            </h3>

            {currentlyReading ? (
              <div className="bg-white/60 backdrop-blur-sm p-4 rounded-xl border border-[#E6D7C3] shadow-md">
                <div className="flex items-start">
                  <Coffee className="text-[#D2691E] text-lg mr-2 mt-1" />
                  <div className="flex-1">
                    {currentlyReading.cover ? (
                      <img
                        src={typeof currentlyReading.cover === 'string' && currentlyReading.cover.startsWith('http') ? 
                             `http://localhost:8088/api/v1/books/cover/${currentlyReading.id}` : 
                             `data:image/jpeg;base64,${currentlyReading.cover}`}
                        alt={currentlyReading.title}
                        className="w-[100px] h-[150px] object-cover rounded-lg mb-4"
                      />
                    ) : (
                      <div className="w-[100px] h-[150px] bg-[#F4E3C1] rounded-lg mb-4 flex items-center justify-center">
                        <p className="text-[#6A5E4D] text-xs">No Cover</p>
                      </div>
                    )}
                    <h6 className="font-playfair text-[#4B3F30] font-semibold mb-2 text-base leading-tight">
                      {currentlyReading.title}
                    </h6>
                    <p className="text-[#6A5E4D] mb-4 font-inter text-sm">
                      by {currentlyReading.authorName}
                    </p>

                    {/* Progress Bar */}
                    <div className="mb-4">
                      <div className="flex justify-between mb-2">
                        <span className="text-[#6A5E4D] text-xs font-inter">
                          Progress
                        </span>
                        <span className="text-[#4B3F30] text-xs font-inter font-medium">
                          0%
                        </span>
                      </div>
                      <LinearProgress 
                        variant="determinate" 
                        value={0}
                        sx={{
                          height: 6,
                          borderRadius: 12,
                          backgroundColor: '#F4E3C1',
                          '& .MuiLinearProgress-bar': {
                            backgroundColor: '#D2691E',
                            borderRadius: 12
                          }
                        }}
                      />
                    </div>

                    <button className="bg-[#D2691E] text-white text-sm font-medium rounded-md px-3 py-1 hover:bg-[#B85A1A] transition-colors">
                      Update Progress
                    </button>
                  </div>
                </div>
              </div>
            ) : (
              <div className="bg-white/60 backdrop-blur-sm p-4 rounded-xl border border-[#E6D7C3] shadow-md text-center">
                <p className="text-[#6A5E4D] font-inter">
                  {borrowedLoading ? 'Loading...' : 'Add your first book!'}
                </p>
              </div>
            )}
          </div>

          {/* CENTER COLUMN — Trending Today + From Friends */}
          <div className="md:col-span-1">
            {/* Trending Today Section */}
            <div className="mb-6">
              <div className="flex items-center mb-4">
                <TrendingUp className="text-[#D2691E] mr-2 text-2xl" />
                <h3 className="text-xl font-semibold font-playfair text-[#4B3F30]">
                  Trending Today
                </h3>
              </div>

              {allBooksLoading ? (
                <div className="flex justify-center py-8">
                  <CircularProgress sx={{ color: '#D2691E' }} />
                </div>
              ) : trendingBooks.length > 0 ? (
                <div className="flex gap-4 overflow-x-auto pb-2 snap-x">
                  {trendingBooks.map((book) => (
                    <div 
                      key={book.id}
                      className="bg-white/60 backdrop-blur-sm p-3 rounded-lg shadow-sm hover:-translate-y-1 transition-transform cursor-pointer min-w-[110px] max-w-[110px] snap-start"
                      onClick={() => navigate(`/books/${book.id}`)}
                    >
                      {book.cover ? (
                        <img
                          src={typeof book.cover === 'string' && book.cover.startsWith('http') ? 
                               `http://localhost:8088/api/v1/books/cover/${book.id}` : 
                               `data:image/jpeg;base64,${book.cover}`}
                          alt={book.title}
                          className="w-full h-[170px] object-cover rounded-lg mb-3"
                        />
                      ) : (
                        <div className="w-full h-[170px] bg-[#F4E3C1] rounded-lg mb-3 flex items-center justify-center">
                          <p className="text-[#6A5E4D] text-xs">No Cover</p>
                        </div>
                      )}
                      <div>
                        <h4 className="font-playfair text-[#4B3F30] font-semibold mb-1 text-xs leading-tight truncate">
                          {book.title}
                        </h4>
                        <p className="text-[#6A5E4D] mb-2 font-inter text-xs">
                          by {book.authorName}
                        </p>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="bg-white/60 backdrop-blur-sm p-4 rounded-xl border border-[#E6D7C3] shadow-md text-center">
                  <p className="text-[#6A5E4D] font-inter">
                    No trending books available
                  </p>
                </div>
              )}
            </div>

            {/* From Friends Section */}
            <div>
              <h3 className="text-xl font-semibold font-playfair text-[#4B3F30] mb-4">
                From Friends
              </h3>

              <div className="bg-white/60 backdrop-blur-sm p-4 rounded-xl border border-[#E6D7C3] shadow-md text-center">
                <p className="text-[#6A5E4D] font-inter mb-2">
                  Join a book club to connect with others
                </p>
                <p className="text-[#6A5E4D] font-inter text-sm">
                  Social features coming soon!
                </p>
              </div>
            </div>
          </div>

          {/* RIGHT COLUMN — To Read & Favorites */}
          <div className="md:col-span-1">
            {/* To Read Section */}
            <div className="mb-6">
              <h3 className="text-xl font-semibold font-playfair text-[#4B3F30] mb-4">
                To Read
              </h3>
              {myBooksLoading ? (
                <div className="flex justify-center py-8">
                  <CircularProgress sx={{ color: '#D2691E' }} />
                </div>
              ) : toReadBooks.length > 0 ? (
                <div className="grid grid-cols-2 gap-2">
                  {toReadBooks.map((book) => (
                    <div 
                      key={book.id}
                      className="bg-white/60 backdrop-blur-sm rounded-lg shadow-sm hover:-translate-y-1 transition-transform cursor-pointer overflow-hidden"
                      onClick={() => navigate(`/books/${book.id}`)}
                    >
                      {book.cover ? (
                        <img
                          src={typeof book.cover === 'string' && book.cover.startsWith('http') ? 
                               `http://localhost:8088/api/v1/books/cover/${book.id}` : 
                               `data:image/jpeg;base64,${book.cover}`}
                          alt={book.title}
                          className="w-full h-[135px] object-cover"
                        />
                      ) : (
                        <div className="w-full h-[135px] bg-[#F4E3C1] flex items-center justify-center">
                          <p className="text-[#6A5E4D] text-xs">No Cover</p>
                        </div>
                      )}
                    </div>
                  ))}
                </div>
              ) : (
                <div className="bg-white/60 backdrop-blur-sm p-4 rounded-xl border border-[#E6D7C3] shadow-md text-center">
                  <p className="text-[#6A5E4D] font-inter">
                    No books in your to-read list
                  </p>
                </div>
              )}
            </div>

            {/* Favorites Section */}
            <div>
              <h3 className="text-xl font-semibold font-playfair text-[#4B3F30] mb-4">
                Favorites
              </h3>
              {readBooksLoading ? (
                <div className="flex justify-center py-8">
                  <CircularProgress sx={{ color: '#D2691E' }} />
                </div>
              ) : favoriteBooks.length > 0 ? (
                <div className="grid grid-cols-2 gap-2">
                  {favoriteBooks.map((book) => (
                    <div 
                      key={book.id}
                      className="bg-white/60 backdrop-blur-sm rounded-lg shadow-sm hover:-translate-y-1 transition-transform cursor-pointer overflow-hidden"
                      onClick={() => navigate(`/books/${book.id}`)}
                    >
                      {book.cover ? (
                        <img
                          src={typeof book.cover === 'string' && book.cover.startsWith('http') ? 
                               `http://localhost:8088/api/v1/books/cover/${book.id}` : 
                               `data:image/jpeg;base64,${book.cover}`}
                          alt={book.title}
                          className="w-full h-[135px] object-cover"
                        />
                      ) : (
                        <div className="w-full h-[135px] bg-[#F4E3C1] flex items-center justify-center">
                          <p className="text-[#6A5E4D] text-xs">No Cover</p>
                        </div>
                      )}
                    </div>
                  ))}
                </div>
              ) : (
                <div className="bg-white/60 backdrop-blur-sm p-4 rounded-xl border border-[#E6D7C3] shadow-md text-center">
                  <p className="text-[#6A5E4D] font-inter">
                    No favorite books yet
                  </p>
                </div>
              )}
            </div>
          </div>
        </div>

        {/* All Books Section */}
        {searchQuery && (
          <div className="mb-6">
            <h3 className="text-xl font-semibold font-playfair text-[#4B3F30] mb-6">
              Search Results
            </h3>
          </div>
        )}

        {allBooksLoading && (
          <div className="flex justify-center py-8">
            <CircularProgress sx={{ color: '#D2691E' }} />
          </div>
        )}

        {allBooksError && (
          <Alert severity="error" className="mb-6">
            {allBooksError.message || String(allBooksError)}
          </Alert>
        )}

        {allBooks?.content && allBooks.content.length > 0 && (
          <div>
            <h3 className="text-xl font-semibold font-playfair text-[#4B3F30] mb-6">
              All Books
            </h3>

            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 xl:grid-cols-6 gap-6">
              {allBooks.content.map(book => (
                <div key={book.id}>
                  <div 
                    className="bg-white/60 backdrop-blur-sm rounded-xl border border-[#E6D7C3] shadow-md hover:-translate-y-1 transition-transform cursor-pointer overflow-hidden h-full"
                    onClick={() => navigate(`/books/${book.id}`)}
                  >
                    {book.cover ? (
                      <img
                        src={typeof book.cover === 'string' && book.cover.startsWith('http') ? 
                             `http://localhost:8088/api/v1/books/cover/${book.id}` : 
                             `data:image/jpeg;base64,${book.cover}`}
                        alt={book.title}
                        className="w-full h-48 object-cover"
                      />
                    ) : (
                      <div className="w-full h-48 bg-[#F4E3C1] flex items-center justify-center">
                        <p className="text-[#6A5E4D] font-inter">
                          No Cover
                        </p>
                      </div>
                    )}
                    <div className="p-4">
                      <h4 className="font-playfair text-[#4B3F30] font-semibold mb-2 text-sm leading-tight">
                        {book.title}
                      </h4>
                      <p className="text-[#6A5E4D] mb-3 font-inter text-sm">
                        by {book.authorName}
                      </p>
                      <button 
                        className="bg-[#D2691E] text-white text-sm font-medium rounded-md px-3 py-1 hover:bg-[#B85A1A] transition-colors w-full"
                        onClick={(e) => {
                          e.stopPropagation();
                          navigate(`/books/${book.id}`);
                        }}
                      >
                        View Details
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>

            {allBooks && (
              <div className="mt-8">
                <PaginationControls
                  currentPage={page}
                  totalPages={allBooks.totalPages || 0}
                  pageSize={size}
                  totalElements={allBooks.totalElements || 0}
                  onPageChange={setPage}
                  onPageSizeChange={setSize}
                  loading={allBooksLoading}
                />
              </div>
            )}
          </div>
        )}

        {/* Decorative Lamp */}
        <div className="fixed bottom-6 right-6 opacity-10 pointer-events-none z-0">
          <Lightbulb className="text-6xl text-[#4B3F30]" />
        </div>
      </div>
    </div>
  );
};

export default BookListPage;
