import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import { Alert, Card, CardContent, CardMedia, CircularProgress, Container, Typography, Box, Pagination } from '@mui/material';
import React from 'react';
import type { BorrowedBookResponse } from '../app/services/models/BorrowedBookResponse';
import { useReadBooks } from '../hooks/useReadBooks';

const ReadPage: React.FC = () => {
  const { data, loading, error, page, setPage } = useReadBooks();
  const books = data?.content || [];

  const handlePageChange = (_: React.ChangeEvent<unknown>, value: number) => {
    setPage(value - 1); // Material UI Pagination is 1 based! backend is 0 based!
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4 }}>
      <Typography variant="h4" gutterBottom>Read Books</Typography>
      {loading && <CircularProgress />}
      {error && <Alert severity="error">{error.message || String(error)}</Alert>}

      {books.length === 0 && !loading && (
        <div style={{ textAlign: 'center', marginTop: '2rem' }}>
          <Typography variant="h6" color="text.secondary" gutterBottom>
            You haven't read any books yet
          </Typography>
          <Typography variant="body1" color="text.secondary">
            Borrow some books and mark them as read when you finish!
          </Typography>
        </div>
      )}

      <div className="book-list-grid">
        {books.map(book => (
          <div className="book-list-card" key={book.id}>
            <Card sx={{ 
              height: '100%', 
              display: 'flex', 
              flexDirection: 'column', 
              maxWidth: 200,
              border: '2px solid #4caf50'
            }}>
              {book.cover ? (
                <CardMedia
                  component="img"
                  height="120"
                  image={`data:image/jpeg;base64,${book.cover}`}
                  alt={book.title}
                  sx={{ objectFit: 'contain' }}
                />
              ) : (
                <CardMedia
                  component="div"
                  sx={{ height: 120, background: '#eee', display: 'flex', alignItems: 'center', justifyContent: 'center' }}
                >
                  <Typography variant="body2" color="text.secondary">
                    No Cover
                  </Typography>
                </CardMedia>
              )}
              <CardContent sx={{ flexGrow: 1, p: 1.5 }}>
                <Typography variant="subtitle1" component="div" sx={{ fontWeight: 'bold', mb: 0.5, lineHeight: 1.2 }}>
                  {book.title}
                </Typography>
                <Typography variant="body2" color="text.secondary" sx={{ mb: 1 }}>
                  by {book.authorName}
                </Typography>
                <Typography variant="caption" color="success.main" sx={{ fontWeight: 'bold', display: 'flex', alignItems: 'center', gap: 0.5 }}>
                  <CheckCircleIcon sx={{ fontSize: 16 }} />
                  âœ“ Completed
                </Typography>
              </CardContent>
            </Card>
          </div>
        ))}
      </div>
      {data && data.totalPages && data.totalPages > 1 && (
        <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
          <Pagination
            count={data.totalPages}
            page={page + 1}
            onChange={handlePageChange}
            color="primary"
          />
        </Box>
      )}
    </Container>
  );
};

export default ReadPage;
