import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  CircularProgress,
  Alert
} from '@mui/material';
import {
  TrendingUp,
  Coffee,
  AutoStories,
  Favorite,
  Star,
  MenuBook
} from '@mui/icons-material';
import PaginationControls from '../components/PaginationControls';
import { useBooks } from '../hooks/useBooks';
import { useBorrowedBooks } from '../hooks/useBorrowedBooks';
import { useMyBooks } from '../hooks/useMyBooks';
import { useReadBooks } from '../hooks/useReadBooks';

const BookListPage: React.FC = () => {
  const { data: allBooks, loading: allBooksLoading, error: allBooksError, page, setPage, size, setSize } = useBooks();
  const { data: borrowedBooks, loading: borrowedLoading } = useBorrowedBooks();
  const { data: myBooks, loading: myBooksLoading } = useMyBooks();
  const { data: readBooks, loading: readBooksLoading } = useReadBooks();
  const navigate = useNavigate();

  // Get currently reading book (first borrowed book)
  const currentlyReading = borrowedBooks?.content?.[0];

  // Get trending books (first 5 from all books)
  const trendingBooks = allBooks?.content?.slice(0, 5) || [];

  // Get to-read books (my books that aren't borrowed or read)
  const toReadBooks = myBooks?.content?.filter(book => 
    !borrowedBooks?.content?.some(borrowed => borrowed.id === book.id) &&
    !readBooks?.content?.some(read => read.id === book.id)
  ).slice(0, 4) || [];

  // Get favorite books (read books, first 4)
  const favoriteBooks = readBooks?.content?.slice(0, 4) || [];

  const renderBookCover = (book: any, className: string = "") => {
    if (book.cover) {
      return (
        <img
          src={typeof book.cover === 'string' && book.cover.startsWith('http') ? 
               `http://localhost:8088/api/v1/books/cover/${book.id}` : 
               `data:image/jpeg;base64,${book.cover}`}
          alt={book.title}
          className={`object-cover overflow-hidden ${className}`}
        />
      );
    }
    return (
      <div className={`bg-gradient-to-br from-[#F4E3C1] to-[#E6D7C3] flex items-center justify-center ${className}`}>
        <MenuBook className="text-[#8B7355] text-2xl" />
      </div>
    );
  };

  const renderStars = (rating: number) => {
    return Array.from({ length: 5 }, (_, i) => (
      <Star 
        key={i} 
        className={i < rating ? 'text-[#FFD700]' : 'text-gray-300'}
        style={{ fontSize: '1rem' }}
      />
    ));
  };

  return (
    <div className="min-h-screen bg-[#FAF3E3]">
      {/* Hero Section */}
      <div className="bg-gradient-to-r from-[#4B3F30] via-[#5D4A33] to-[#4B3F30] text-white py-16">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
          <h1 className="font-playfair text-4xl md:text-5xl font-bold mb-4">
            Welcome to Your Literary Haven
          </h1>
          <p className="text-xl text-white/90 max-w-2xl mx-auto">
            Discover, track, and share your reading journey in our cozy digital bookstore
          </p>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
        {/* 3-Column Layout */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-6">

          {/* LEFT COLUMN — Currently Reading */}
          <div>
            <h2 className="text-xl font-semibold font-playfair text-[#4B3F30] mb-6">
              Currently Reading
            </h2>

            {currentlyReading ? (
              <div className="bg-white/60 backdrop-blur-sm p-4 rounded-xl border border-[#E6D7C3] shadow-md shadow-[#4B3F30]/10">
                <div className="flex gap-4 mb-4">
                  {renderBookCover(currentlyReading, "w-[100px] h-[150px] rounded-lg")}
                  <div className="flex-1">
                    <h3 className="font-playfair text-lg font-semibold text-[#4B3F30] mb-2 leading-tight">
                      {currentlyReading.title}
                    </h3>
                    <p className="text-[#6A5E4D] mb-4 font-inter text-sm">
                      by {currentlyReading.authorName}
                    </p>

                    {/* Progress Bar */}
                    <div className="mb-4">
                      <div className="flex justify-between mb-2">
                        <span className="text-[#6A5E4D] text-sm font-inter">Progress</span>
                        <span className="text-[#4B3F30] text-sm font-inter font-medium">0%</span>
                      </div>
                      <div className="w-full bg-[#F4E3C1] rounded-full h-2">
                        <div className="bg-[#D2691E] h-2 rounded-full transition-all duration-300" style={{ width: '0%' }}></div>
                      </div>
                    </div>

                    <button className="bg-[#D2691E] text-white text-sm font-medium rounded-md px-3 py-1 hover:bg-[#B85A1A] transition-colors w-full">
                      Update Progress
                    </button>
                  </div>
                </div>
              </div>
            ) : (
              <div className="bg-white/60 backdrop-blur-sm p-4 rounded-xl border border-[#E6D7C3] shadow-md shadow-[#4B3F30]/10 text-center">
                <Coffee className="text-[#D2691E] text-4xl mb-3 mx-auto" />
                <p className="text-[#6A5E4D] font-inter">
                  {borrowedLoading ? 'Loading...' : 'Add your first book!'}
                </p>
              </div>
            )}
          </div>

          {/* CENTER COLUMN — Trending Today + From Friends */}
          <div>
            {/* Trending Today Section */}
            <div className="mb-6">
              <h2 className="text-xl font-semibold font-playfair text-[#4B3F30] mb-6">
                Trending Today
              </h2>

              {allBooksLoading ? (
                <div className="bg-white/60 backdrop-blur-sm p-4 rounded-xl border border-[#E6D7C3] shadow-md shadow-[#4B3F30]/10 flex justify-center py-8">
                  <CircularProgress sx={{ color: '#D2691E' }} />
                </div>
              ) : trendingBooks.length > 0 ? (
                <div className="bg-white/60 backdrop-blur-sm p-4 rounded-xl border border-[#E6D7C3] shadow-md shadow-[#4B3F30]/10">
                  <div className="flex gap-4 overflow-x-auto snap-x pb-2">
                    {trendingBooks.map((book) => (
                      <div 
                        key={book.id}
                        className="w-[110px] h-[170px] rounded-lg shadow-sm hover:-translate-y-1 transition-transform cursor-pointer flex-shrink-0 snap-start bg-white/80 p-2"
                        onClick={() => navigate(`/books/${book.id}`)}
                      >
                        {renderBookCover(book, "w-full h-[120px] rounded-lg mb-2")}
                        <h4 className="font-playfair text-xs font-semibold text-[#4B3F30] leading-tight line-clamp-2">
                          {book.title}
                        </h4>
                        <p className="text-[#6A5E4D] text-xs font-inter truncate">
                          {book.authorName}
                        </p>
                      </div>
                    ))}
                  </div>
                </div>
              ) : (
                <div className="bg-white/60 backdrop-blur-sm p-4 rounded-xl border border-[#E6D7C3] shadow-md shadow-[#4B3F30]/10 text-center">
                  <TrendingUp className="text-[#D2691E] text-4xl mb-3 mx-auto" />
                  <p className="text-[#6A5E4D] font-inter">
                    No trending books available
                  </p>
                </div>
              )}
            </div>

            {/* From Friends Section */}
            <div>
              <h2 className="text-xl font-semibold font-playfair text-[#4B3F30] mb-6">
                From Friends
              </h2>
              <div className="bg-white/60 backdrop-blur-sm p-4 rounded-xl border border-[#E6D7C3] shadow-md shadow-[#4B3F30]/10 text-center">
                <Favorite className="text-[#D2691E] text-4xl mb-3 mx-auto" />
                <p className="text-[#6A5E4D] font-inter mb-2">
                  Join a book club to connect with others
                </p>
                <p className="text-[#8B7355] font-inter text-sm">
                  Social features coming soon!
                </p>
              </div>
            </div>
          </div>

          {/* RIGHT COLUMN — To Read & Favorites */}
          <div>
            {/* To Read Section */}
            <div className="mb-6">
              <h2 className="text-xl font-semibold font-playfair text-[#4B3F30] mb-6">
                To Read
              </h2>
              {myBooksLoading ? (
                <div className="bg-white/60 backdrop-blur-sm p-4 rounded-xl border border-[#E6D7C3] shadow-md shadow-[#4B3F30]/10 flex justify-center py-8">
                  <CircularProgress sx={{ color: '#D2691E' }} size={24} />
                </div>
              ) : toReadBooks.length > 0 ? (
                <div className="bg-white/60 backdrop-blur-sm p-4 rounded-xl border border-[#E6D7C3] shadow-md shadow-[#4B3F30]/10">
                  <div className="grid grid-cols-2 gap-3">
                    {toReadBooks.map((book) => (
                      <div 
                        key={book.id}
                        className="cursor-pointer hover:-translate-y-1 transition-transform"
                        onClick={() => navigate(`/books/${book.id}`)}
                      >
                        {renderBookCover(book, "w-full h-[135px] rounded-lg")}
                      </div>
                    ))}
                  </div>
                </div>
              ) : (
                <div className="bg-white/60 backdrop-blur-sm p-4 rounded-xl border border-[#E6D7C3] shadow-md shadow-[#4B3F30]/10 text-center">
                  <AutoStories className="text-[#D2691E] text-4xl mb-3 mx-auto" />
                  <p className="text-[#6A5E4D] font-inter">
                    Build your reading list
                  </p>
                </div>
              )}
            </div>

            {/* Favorites Section */}
            <div>
              <h2 className="text-xl font-semibold font-playfair text-[#4B3F30] mb-6">
                Favorites
              </h2>
              {readBooksLoading ? (
                <div className="bg-white/60 backdrop-blur-sm p-4 rounded-xl border border-[#E6D7C3] shadow-md shadow-[#4B3F30]/10 flex justify-center py-8">
                  <CircularProgress sx={{ color: '#D2691E' }} size={24} />
                </div>
              ) : favoriteBooks.length > 0 ? (
                <div className="bg-white/60 backdrop-blur-sm p-4 rounded-xl border border-[#E6D7C3] shadow-md shadow-[#4B3F30]/10">
                  <div className="grid grid-cols-2 gap-3">
                    {favoriteBooks.map((book) => (
                      <div 
                        key={book.id}
                        className="cursor-pointer hover:-translate-y-1 transition-transform"
                        onClick={() => navigate(`/books/${book.id}`)}
                      >
                        {renderBookCover(book, "w-full h-[135px] rounded-lg")}
                      </div>
                    ))}
                  </div>
                </div>
              ) : (
                <div className="bg-white/60 backdrop-blur-sm p-4 rounded-xl border border-[#E6D7C3] shadow-md shadow-[#4B3F30]/10 text-center">
                  <Star className="text-[#FFD700] text-4xl mb-3 mx-auto" />
                  <p className="text-[#6A5E4D] font-inter">
                    No favorites yet
                  </p>
                </div>
              )}
            </div>
          </div>
        </div>

        {/* All Books Section */}
        {allBooksLoading && (
          <div className="flex justify-center py-12">
            <CircularProgress sx={{ color: '#D2691E' }} size={48} />
          </div>
        )}

        {allBooksError && (
          <Alert severity="error" className="mb-8">
            {allBooksError.message || String(allBooksError)}
          </Alert>
        )}

        {allBooks?.content && allBooks.content.length > 0 && (
          <div>
            <h2 className="text-xl font-semibold font-playfair text-[#4B3F30] mb-6">
              Discover Books
            </h2>

            <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 xl:grid-cols-6 gap-6">
              {allBooks.content.map(book => (
                <div key={book.id}>
                  <div 
                    className="bg-white/60 backdrop-blur-sm rounded-xl border border-[#E6D7C3] shadow-md shadow-[#4B3F30]/10 hover:-translate-y-1 transition-transform cursor-pointer overflow-hidden h-full p-3"
                    onClick={() => navigate(`/books/${book.id}`)}
                  >
                    {renderBookCover(book, "w-full h-48 rounded-lg mb-4")}
                    <div>
                      <h4 className="font-playfair text-sm font-semibold text-[#4B3F30] mb-2 leading-tight line-clamp-2">
                        {book.title}
                      </h4>
                      <p className="text-[#6A5E4D] mb-3 text-xs font-inter line-clamp-1">
                        by {book.authorName}
                      </p>
                      <button 
                        className="bg-[#D2691E] text-white text-xs font-medium rounded-md px-3 py-1 hover:bg-[#B85A1A] transition-colors w-full"
                        onClick={(e) => {
                          e.stopPropagation();
                          navigate(`/books/${book.id}`);
                        }}
                      >
                        View Details
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>

            {allBooks && (
              <div className="mt-12">
                <PaginationControls
                  currentPage={page}
                  totalPages={allBooks.totalPages || 0}
                  pageSize={size}
                  totalElements={allBooks.totalElements || 0}
                  onPageChange={setPage}
                  onPageSizeChange={setSize}
                  loading={allBooksLoading}
                />
              </div>
            )}
          </div>
        )}
      </div>

      {/* Mobile Bottom Navigation */}
      <div className="fixed bottom-0 inset-x-0 bg-[#FAF3E3] flex justify-around p-2 shadow-inner border-t border-[#E6D7C3] md:hidden">
        <button 
          onClick={() => navigate('/books')}
          className="flex flex-col items-center p-2 text-[#4B3F30] hover:text-[#D2691E]"
        >
          <AutoStories className="text-lg" />
          <span className="text-xs font-inter">Home</span>
        </button>
        <button 
          onClick={() => navigate('/my-books')}
          className="flex flex-col items-center p-2 text-[#4B3F30] hover:text-[#D2691E]"
        >
          <MenuBook className="text-lg" />
          <span className="text-xs font-inter">My Shelf</span>
        </button>
        <button 
          onClick={() => navigate('/books')}
          className="flex flex-col items-center p-2 text-[#4B3F30] hover:text-[#D2691E]"
        >
          <TrendingUp className="text-lg" />
          <span className="text-xs font-inter">Explore</span>
        </button>
        <button 
          onClick={() => navigate('/profile/1')}
          className="flex flex-col items-center p-2 text-[#4B3F30] hover:text-[#D2691E]"
        >
          <Favorite className="text-lg" />
          <span className="text-xs font-inter">Profile</span>
        </button>
      </div>
    </div>
  );
};

export default BookListPage;
