import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  CircularProgress,
  Alert,
  Avatar,
  LinearProgress,
  IconButton
} from '@mui/material';
import {
  FavoriteBorder,
  Star,
  TrendingUp,
  Coffee,
  Lightbulb
} from '@mui/icons-material';
import PaginationControls from '../components/PaginationControls';
import { useBooks } from '../hooks/useBooks';

// Mock currently reading data - in a real app, this would come from an API
const currentlyReading = {
  title: "Harry Potter and the Sorcerer's Stone",
  author: "J.K. Rowling",
  progress: 65,
  cover: "https://images.unsplash.com/photo-1603871165848-0aa92c869fa1?w=100&h=150&fit=crop"
};

// Mock trending books data - in a real app, this would come from an API
const trendingBooks = [
  {
    id: 1,
    title: "Harry Potter and the Sorcerer's Stone",
    author: "J.K. Rowling",
    rating: 4.5,
    cover: "https://images.unsplash.com/photo-1603871165848-0aa92c869fa1?w=110&h=170&fit=crop"
  },
  {
    id: 2,
    title: "It Ends with Us",
    author: "Colleen Hoover",
    rating: 4.3,
    cover: "https://images.unsplash.com/photo-1544947950-fa07a98d237f?w=110&h=170&fit=crop"
  },
  {
    id: 3,
    title: "The Midnight Library",
    author: "Matt Haig",
    rating: 4.3,
    cover: "https://images.unsplash.com/photo-1481627834876-b7833e8f5570?w=110&h=170&fit=crop"
  },
  {
    id: 4,
    title: "Circe",
    author: "Madeline Miller",
    rating: 4.4,
    cover: "https://images.unsplash.com/photo-1512820790803-83ca734da794?w=110&h=170&fit=crop"
  },
  {
    id: 5,
    title: "The Seven Husbands of Evelyn Hugo",
    author: "Taylor Jenkins Reid",
    rating: 4.2,
    cover: "https://images.unsplash.com/photo-1543002588-bfa74002ed7e?w=110&h=170&fit=crop"
  }
];

// Mock friends feed data
const friendsFeed = [
  {
    id: 1,
    name: "Emma Robinson",
    avatar: "E",
    bookTitle: "The Silent Patient",
    rating: 4.5,
    review: "An engaging, fast-paced mystery with intriguing characters and lots of twists. I really enjoyed it!",
    likes: 15,
    comments: 4
  }
];

// Mock to-read and favorites data
const toReadBooks = [
  { id: 1, cover: "https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=90&h=135&fit=crop" },
  { id: 2, cover: "https://images.unsplash.com/photo-1544947950-fa07a98d237f?w=90&h=135&fit=crop" },
  { id: 3, cover: "https://images.unsplash.com/photo-1481627834876-b7833e8f5570?w=90&h=135&fit=crop" },
  { id: 4, cover: "https://images.unsplash.com/photo-1512820790803-83ca734da794?w=90&h=135&fit=crop" }
];

const favoriteBooks = [
  { id: 1, cover: "https://images.unsplash.com/photo-1603871165848-0aa92c869fa1?w=90&h=135&fit=crop" },
  { id: 2, cover: "https://images.unsplash.com/photo-1544947950-fa07a98d237f?w=90&h=135&fit=crop" }
];

const BookListPage: React.FC = () => {
  const { data, loading, error, page, setPage, size, setSize } = useBooks();
  const navigate = useNavigate();
  const [searchQuery] = useState('');

  const renderStars = (rating: number) => {
    return Array.from({ length: 5 }, (_, i) => (
      <Star 
        key={i} 
        sx={{ 
          color: i < rating ? '#FFD700' : '#E0E0E0',
          fontSize: '0.75rem'
        }} 
      />
    ));
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-vintage-background to-vintage-background-secondary py-6 px-6 relative">
      {/* Background Pattern */}
      <div 
        className="absolute inset-0 opacity-30 pointer-events-none"
        style={{
          backgroundImage: `url("data:image/svg+xml,%3Csvg width='100' height='100' xmlns='http://www.w3.org/2000/svg'%3E%3Cdefs%3E%3Cpattern id='bookshelf' x='0' y='0' width='100' height='100' patternUnits='userSpaceOnUse'%3E%3Crect width='100' height='100' fill='none'/%3E%3Crect x='10' y='20' width='80' height='4' fill='%23E6D7C3' opacity='0.1'/%3E%3Crect x='10' y='40' width='80' height='4' fill='%23E6D7C3' opacity='0.1'/%3E%3Crect x='10' y='60' width='80' height='4' fill='%23E6D7C3' opacity='0.1'/%3E%3C/pattern%3E%3C/defs%3E%3Crect width='100%25' height='100%25' fill='url(%23bookshelf)'/%3E%3C/svg%3E")`
        }}
      />

      <div className="max-w-7xl mx-auto relative z-10">
        {/* 3-Column Layout */}
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8 mb-8">

          {/* LEFT PANEL — Currently Reading */}
          <div className="lg:col-span-1">
            <h3 className="font-heading text-vintage-text-primary font-semibold mb-6 text-2xl">
              Currently Reading
            </h3>

            {currentlyReading ? (
              <div className="vintage-card relative">
                <div className="flex items-start mb-4">
                  <Coffee className="text-vintage-text-muted text-lg mr-2 mt-1" />
                  <div className="flex-1">
                    <img
                      src={currentlyReading.cover}
                      alt={currentlyReading.title}
                      className="w-24 h-36 object-cover rounded-md mb-4"
                    />
                    <h6 className="font-heading text-vintage-text-primary font-semibold mb-2 text-base leading-tight">
                      {currentlyReading.title}
                    </h6>
                    <p className="text-vintage-text-muted mb-4 font-body text-sm">
                      by {currentlyReading.author}
                    </p>

                    {/* Progress Bar */}
                    <div className="mb-4">
                      <div className="flex justify-between mb-2">
                        <span className="text-vintage-text-muted text-xs font-body">
                          Progress
                        </span>
                        <span className="text-vintage-text-primary text-xs font-body font-medium">
                          {currentlyReading.progress}%
                        </span>
                      </div>
                      <LinearProgress 
                        variant="determinate" 
                        value={currentlyReading.progress}
                        sx={{
                          height: 6,
                          borderRadius: 12,
                          backgroundColor: '#F4E3C1',
                          '& .MuiLinearProgress-bar': {
                            backgroundColor: '#D2691E',
                            borderRadius: 12
                          }
                        }}
                      />
                    </div>

                    <button className="btn-primary text-sm">
                      Update Progress
                    </button>
                  </div>
                </div>
              </div>
            ) : (
              <div className="vintage-card text-center">
                <p className="text-vintage-text-muted font-body">
                  Add your first book!
                </p>
              </div>
            )}
          </div>

          {/* CENTER PANEL — Trending Today + From Friends */}
          <div className="lg:col-span-1">
            {/* Trending Today Section */}
            <div className="mb-8">
              <div className="flex items-center mb-4">
                <TrendingUp className="text-vintage-accent-orange mr-2 text-2xl" />
                <h3 className="font-heading text-vintage-text-primary font-semibold text-2xl">
                  Trending Today
                </h3>
              </div>

              <div className="flex gap-4 overflow-x-auto pb-2 scrollbar-thin scrollbar-track-vintage-background-secondary scrollbar-thumb-vintage-border">
                {trendingBooks.map((book) => (
                  <div 
                    key={book.id}
                    className="book-card min-w-[110px] max-w-[110px]"
                    onClick={() => navigate(`/books/${book.id}`)}
                  >
                    <img
                      src={book.cover}
                      alt={book.title}
                      className="w-full h-[170px] object-cover"
                    />
                    <div className="p-3">
                      <h4 className="font-heading text-vintage-text-primary font-semibold mb-1 text-xs leading-tight truncate">
                        {book.title}
                      </h4>
                      <p className="text-vintage-text-muted mb-2 font-body text-xs">
                        by {book.author}
                      </p>
                      {book.rating && (
                        <div className="flex items-center">
                          {renderStars(book.rating)}
                          <span className="ml-1 text-vintage-text-muted font-body text-xs">
                            {book.rating}
                          </span>
                        </div>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            </div>

            {/* From Friends Section */}
            <div>
              <h3 className="font-heading text-vintage-text-primary font-semibold mb-4 text-2xl">
                From Friends
              </h3>

              {friendsFeed.length > 0 ? (
                friendsFeed.map((post) => (
                  <div key={post.id} className="vintage-card mb-4">
                    <div className="flex items-start mb-4">
                      <Avatar 
                        sx={{ 
                          width: 40,
                          height: 40,
                          bgcolor: '#D2691E',
                          mr: 2,
                          fontFamily: 'Inter, sans-serif',
                          fontWeight: 500,
                          fontSize: '0.875rem'
                        }}
                      >
                        {post.avatar}
                      </Avatar>
                      <div className="flex-1">
                        <h5 className="font-body text-vintage-text-primary font-semibold mb-1 text-sm">
                          {post.name}
                        </h5>
                        <p className="text-vintage-text-muted font-body text-sm mb-2">
                          {post.bookTitle} • {post.rating}★
                        </p>
                        <p className="text-vintage-text-primary font-body text-sm leading-relaxed mb-4">
                          {post.review}
                        </p>

                        <div className="flex items-center gap-4">
                          <IconButton size="small" sx={{ color: '#6A5E4D' }}>
                            <FavoriteBorder sx={{ fontSize: '1rem' }} />
                          </IconButton>
                          <span className="text-vintage-text-muted font-body text-xs">
                            {post.likes}
                          </span>
                          <span className="text-vintage-text-muted font-body text-xs">
                            💬 {post.comments}
                          </span>
                        </div>
                      </div>
                    </div>
                  </div>
                ))
              ) : (
                <div className="vintage-card text-center">
                  <p className="text-vintage-text-muted font-body mb-2">
                    Looking for friends?
                  </p>
                  <p className="text-vintage-text-muted font-body text-sm">
                    Join a book club to see reviews from other readers!
                  </p>
                </div>
              )}
            </div>
          </div>

          {/* RIGHT PANEL — To Read & Favorites */}
          <div className="lg:col-span-1">
            {/* To Read Section */}
            <div className="mb-8">
              <h3 className="font-heading text-vintage-text-primary font-semibold mb-4 text-2xl">
                To Read
              </h3>
              <div className="grid grid-cols-2 gap-2">
                {toReadBooks.map((book) => (
                  <div 
                    key={book.id}
                    className="book-card"
                  >
                    <img
                      src={book.cover}
                      alt="Book cover"
                      className="w-full h-[135px] object-cover"
                    />
                  </div>
                ))}
              </div>
            </div>

            {/* Favorites Section */}
            <div>
              <h3 className="font-heading text-vintage-text-primary font-semibold mb-4 text-2xl">
                Favorites
              </h3>
              <div className="grid grid-cols-2 gap-2">
                {favoriteBooks.map((book) => (
                  <div 
                    key={book.id}
                    className="book-card"
                  >
                    <img
                      src={book.cover}
                      alt="Book cover"
                      className="w-full h-[135px] object-cover"
                    />
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>

        {/* All Books Section */}
        {searchQuery && (
          <div className="mb-8">
            <h3 className="font-heading text-vintage-text-primary font-semibold mb-6 text-2xl">
              Search Results
            </h3>
          </div>
        )}

        {loading && (
          <div className="flex justify-center py-8">
            <CircularProgress sx={{ color: '#D2691E' }} />
          </div>
        )}

        {error && (
          <Alert severity="error" className="mb-6">
            {error.message || String(error)}
          </Alert>
        )}

        {data?.content && data.content.length > 0 && (
          <div>
            <h3 className="font-heading text-vintage-text-primary font-semibold mb-6 text-2xl">
              All Books
            </h3>

            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 xl:grid-cols-6 gap-6">
              {data.content.map(book => (
                <div key={book.id}>
                  <div 
                    className="book-card h-full"
                    onClick={() => navigate(`/books/${book.id}`)}
                  >
                    {book.cover ? (
                      <img
                        src={typeof book.cover === 'string' && book.cover.startsWith('http') ? 
                             `http://localhost:8088/api/v1/books/cover/${book.id}` : 
                             `data:image/jpeg;base64,${book.cover}`}
                        alt={book.title}
                        className="w-full h-48 object-cover"
                      />
                    ) : (
                      <div className="w-full h-48 bg-vintage-background-secondary flex items-center justify-center">
                        <p className="text-vintage-text-muted font-body">
                          No Cover
                        </p>
                      </div>
                    )}
                    <div className="p-4">
                      <h4 className="font-heading text-vintage-text-primary font-semibold mb-2 text-sm leading-tight">
                        {book.title}
                      </h4>
                      <p className="text-vintage-text-muted mb-3 font-body text-sm">
                        by {book.authorName}
                      </p>
                      <button 
                        className="btn-secondary w-full text-sm"
                        onClick={(e) => {
                          e.stopPropagation();
                          navigate(`/books/${book.id}`);
                        }}
                      >
                        View Details
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>

            {data && (
              <div className="mt-8">
                <PaginationControls
                  currentPage={page}
                  totalPages={data.totalPages || 0}
                  pageSize={size}
                  totalElements={data.totalElements || 0}
                  onPageChange={setPage}
                  onPageSizeChange={setSize}
                  loading={loading}
                />
              </div>
            )}
          </div>
        )}

        {/* Decorative Lamp */}
        <div className="fixed bottom-6 right-6 opacity-10 pointer-events-none z-0">
          <Lightbulb className="text-6xl text-vintage-text-primary" />
        </div>
      </div>
    </div>
  );
};

export default BookListPage;
