import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Typography,
  Box,
  Card,
  CardContent,
  CardMedia,
  Button,
  CircularProgress,
  Alert,
  Grid,
  Avatar
} from '@mui/material';
import {
  FavoriteBorder,
  Star,
  TrendingUp,
  Coffee,
  LocalLibrary
} from '@mui/icons-material';
import PaginationControls from '../components/PaginationControls';
import { useBooks } from '../hooks/useBooks';

// Mock trending books data - in a real app, this would come from an API
const trendingBooks = [
  {
    id: 1,
    title: "Harry Potter and the Sorcerer's Stone",
    author: "J.K. Rowling",
    rating: 4.5,
    ratings: "2.5k",
    cover: "https://images.unsplash.com/photo-1603871165848-0aa92c869fa1?w=200&h=300&fit=crop"
  },
  {
    id: 2,
    title: "It Ends with Us",
    author: "Colleen Hoover",
    rating: 4.3,
    ratings: "1.2M",
    cover: "https://images.unsplash.com/photo-1544947950-fa07a98d237f?w=200&h=300&fit=crop"
  },
  {
    id: 3,
    title: "The Midnight Library",
    author: "Matt Haig",
    rating: 4.3,
    ratings: "8.45k",
    cover: "https://images.unsplash.com/photo-1481627834876-b7833e8f5570?w=200&h=300&fit=crop"
  },
  {
    id: 4,
    title: "Circe",
    author: "Madeline Miller",
    rating: 4.4,
    ratings: "770k",
    cover: "https://images.unsplash.com/photo-1512820790803-83ca734da794?w=200&h=300&fit=crop"
  }
];

// Mock currently reading data
const currentlyReading = {
  title: "Harry Potter and the Sorcerer's Stone",
  author: "J.K. Rowling",
  progress: 65,
  cover: "https://images.unsplash.com/photo-1603871165848-0aa92c869fa1?w=200&h=300&fit=crop"
};

// Mock friends feed data
const friendsFeed = [
  {
    id: 1,
    name: "Emma Robinson",
    avatar: "E",
    time: "2 hours ago",
    review: "An engaging, fast-paced mystery with intriguing characters and lots of twists. I really enjoyed it!",
    book: "The Silent Patient",
    likes: 15,
    comments: 4
  }
];

const BookListPage: React.FC = () => {
  const { data, loading, error, page, setPage, size, setSize } = useBooks();
  const navigate = useNavigate();
  const [searchQuery] = useState('');

  const renderStars = (rating: number) => {
    return Array.from({ length: 5 }, (_, i) => (
      <Star 
        key={i} 
        sx={{ 
          color: i < rating ? '#FFD700' : '#E0E0E0',
          fontSize: '1rem'
        }} 
      />
    ));
  };

  return (
    <Box sx={{ 
      backgroundColor: 'var(--background-primary)',
      minHeight: '100vh',
      py: 4
    }}>
      <Container maxWidth="xl">
        {/* Trending Today Section */}
        <Box sx={{ mb: 6 }}>
          <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
            <TrendingUp sx={{ color: 'var(--accent-orange)', mr: 1, fontSize: '2rem' }} />
            <Typography 
              variant="h2" 
              sx={{ 
                fontFamily: 'var(--font-heading)',
                color: 'var(--text-primary)',
                fontWeight: 700
              }}
            >
              Trending Today
            </Typography>
          </Box>

          <Box sx={{ 
            display: 'flex', 
            gap: 3, 
            overflowX: 'auto',
            pb: 2,
            '&::-webkit-scrollbar': {
              height: 8,
            },
            '&::-webkit-scrollbar-track': {
              backgroundColor: 'var(--background-secondary)',
              borderRadius: 4,
            },
            '&::-webkit-scrollbar-thumb': {
              backgroundColor: 'var(--border-color)',
              borderRadius: 4,
            }
          }}>
            {trendingBooks.map((book) => (
              <Card 
                key={book.id}
                className="book-card"
                sx={{ 
                  minWidth: 200,
                  maxWidth: 200,
                  cursor: 'pointer',
                  '&:hover': {
                    transform: 'translateY(-8px)',
                    boxShadow: 'var(--shadow-medium)'
                  }
                }}
                onClick={() => navigate(`/books/${book.id}`)}
              >
                <CardMedia
                  component="img"
                  height="280"
                  image={book.cover}
                  alt={book.title}
                  sx={{ objectFit: 'cover' }}
                />
                <CardContent sx={{ p: 2 }}>
                  <Typography 
                    variant="subtitle1" 
                    sx={{ 
                      fontWeight: 600,
                      fontFamily: 'var(--font-heading)',
                      color: 'var(--text-primary)',
                      mb: 0.5,
                      lineHeight: 1.3
                    }}
                  >
                    {book.title}
                  </Typography>
                  <Typography 
                    variant="body2" 
                    sx={{ 
                      color: 'var(--text-secondary)',
                      mb: 1,
                      fontFamily: 'var(--font-body)'
                    }}
                  >
                    by {book.author}
                  </Typography>
                  <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                    {renderStars(book.rating)}
                    <Typography 
                      variant="body2" 
                      sx={{ 
                        ml: 1,
                        color: 'var(--text-muted)',
                        fontFamily: 'var(--font-body)'
                      }}
                    >
                      {book.rating}
                    </Typography>
                  </Box>
                  <Typography 
                    variant="caption" 
                    sx={{ 
                      color: 'var(--text-muted)',
                      fontFamily: 'var(--font-body)'
                    }}
                  >
                    {book.ratings} ratings
                  </Typography>
                </CardContent>
              </Card>
            ))}
          </Box>
        </Box>

        {/* Middle Section - Split Layout */}
        <Grid container spacing={4} sx={{ mb: 6 }}>
          {/* Left: Currently Reading */}
          <Grid item xs={12} md={4}>
            <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
              <LocalLibrary sx={{ color: 'var(--accent-navy)', mr: 1, fontSize: '1.5rem' }} />
              <Typography 
                variant="h3" 
                sx={{ 
                  fontFamily: 'var(--font-heading)',
                  color: 'var(--text-primary)',
                  fontWeight: 600
                }}
              >
                Currently Reading
              </Typography>
            </Box>

            <Card className="cozy-card" sx={{ 
              position: 'relative',
              overflow: 'hidden',
              '&::before': {
                content: '""',
                position: 'absolute',
                top: 0,
                left: 0,
                right: 0,
                bottom: 0,
                background: 'linear-gradient(135deg, rgba(230, 215, 195, 0.1) 0%, rgba(255, 255, 255, 0.9) 100%)',
                zIndex: 0
              }
            }}>
              <Box sx={{ position: 'relative', zIndex: 1, display: 'flex', alignItems: 'center' }}>
                <CardMedia
                  component="img"
                  sx={{ 
                    width: 80, 
                    height: 120, 
                    objectFit: 'cover',
                    borderRadius: 'var(--radius-sm)',
                    mr: 3
                  }}
                  image={currentlyReading.cover}
                  alt={currentlyReading.title}
                />
                <Box sx={{ flex: 1 }}>
                  <Typography 
                    variant="h6" 
                    sx={{ 
                      fontFamily: 'var(--font-heading)',
                      color: 'var(--text-primary)',
                      fontWeight: 600,
                      mb: 1
                    }}
                  >
                    {currentlyReading.title}
                  </Typography>
                  <Typography 
                    variant="body2" 
                    sx={{ 
                      color: 'var(--text-secondary)',
                      mb: 2,
                      fontFamily: 'var(--font-body)'
                    }}
                  >
                    by {currentlyReading.author}
                  </Typography>

                  {/* Progress Bar */}
                  <Box sx={{ mb: 2 }}>
                    <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                      <Typography 
                        variant="body2" 
                        sx={{ 
                          color: 'var(--text-muted)',
                          fontFamily: 'var(--font-body)'
                        }}
                      >
                        Progress
                      </Typography>
                      <Typography 
                        variant="body2" 
                        sx={{ 
                          color: 'var(--text-primary)',
                          fontFamily: 'var(--font-body)',
                          fontWeight: 500
                        }}
                      >
                        {currentlyReading.progress}%
                      </Typography>
                    </Box>
                    <Box sx={{ 
                      width: '100%', 
                      height: 8, 
                      backgroundColor: 'var(--background-secondary)',
                      borderRadius: 4,
                      overflow: 'hidden'
                    }}>
                      <Box sx={{ 
                        width: `${currentlyReading.progress}%`, 
                        height: '100%', 
                        backgroundColor: 'var(--accent-orange)',
                        borderRadius: 4
                      }} />
                    </Box>
                  </Box>

                  <Button 
                    variant="contained"
                    sx={{
                      backgroundColor: 'var(--accent-orange)',
                      color: 'white',
                      fontFamily: 'var(--font-body)',
                      fontWeight: 500,
                      '&:hover': {
                        backgroundColor: '#B85A1A'
                      }
                    }}
                  >
                    Update Progress
                  </Button>
                </Box>
              </Box>

              {/* Decorative coffee cup */}
              <Box sx={{ 
                position: 'absolute', 
                bottom: 16, 
                right: 16,
                opacity: 0.1
              }}>
                <Coffee sx={{ fontSize: '3rem', color: 'var(--text-primary)' }} />
              </Box>
            </Card>
          </Grid>

          {/* Center: Trending Today + From Friends */}
          <Grid item xs={12} md={4}>
            <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
              <Typography 
                variant="h3" 
                sx={{ 
                  fontFamily: 'var(--font-heading)',
                  color: 'var(--text-primary)',
                  fontWeight: 600
                }}
              >
                From Friends
              </Typography>
            </Box>

            {friendsFeed.map((post) => (
              <Card key={post.id} className="cozy-card" sx={{ mb: 3 }}>
                <Box sx={{ display: 'flex', alignItems: 'flex-start', mb: 2 }}>
                  <Avatar 
                    sx={{ 
                      bgcolor: 'var(--accent-orange)',
                      mr: 2,
                      fontFamily: 'var(--font-body)',
                      fontWeight: 500
                    }}
                  >
                    {post.avatar}
                  </Avatar>
                  <Box sx={{ flex: 1 }}>
                    <Typography 
                      variant="subtitle1" 
                      sx={{ 
                        fontWeight: 600,
                        color: 'var(--text-primary)',
                        fontFamily: 'var(--font-body)'
                      }}
                    >
                      {post.name}
                    </Typography>
                    <Typography 
                      variant="caption" 
                      sx={{ 
                        color: 'var(--text-muted)',
                        fontFamily: 'var(--font-body)'
                      }}
                    >
                      {post.time}
                    </Typography>
                  </Box>
                </Box>

                <Typography 
                  variant="body1" 
                  sx={{ 
                    color: 'var(--text-primary)',
                    mb: 1,
                    fontFamily: 'var(--font-body)',
                    lineHeight: 1.6
                  }}
                >
                  {post.review}
                </Typography>

                <Typography 
                  variant="body2" 
                  sx={{ 
                    color: 'var(--accent-navy)',
                    fontWeight: 500,
                    mb: 2,
                    fontFamily: 'var(--font-body)'
                  }}
                >
                   {post.book}
                </Typography>

                <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 0.5 }}>
                    <FavoriteBorder sx={{ color: 'var(--text-muted)', fontSize: '1.2rem' }} />
                    <Typography 
                      variant="body2" 
                      sx={{ 
                        color: 'var(--text-muted)',
                        fontFamily: 'var(--font-body)'
                      }}
                    >
                      {post.likes}
                    </Typography>
                  </Box>
                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 0.5 }}>
                    <Typography 
                      variant="body2" 
                      sx={{ 
                        color: 'var(--text-muted)',
                        fontFamily: 'var(--font-body)'
                      }}
                    >
                       {post.comments}
                    </Typography>
                  </Box>
                </Box>
              </Card>
            ))}
          </Grid>
        </Grid>

        {/* Bottom Section - Shelves */}
        <Grid container spacing={4} sx={{ mb: 6 }}>
          {/* To Read Shelf */}
          <Grid item xs={12} md={6} component="div">
            <Typography 
              variant="h3" 
              sx={{ 
                fontFamily: 'var(--font-heading)',
                color: 'var(--text-primary)',
                fontWeight: 600,
                mb: 3
              }}
            >
              To Read
            </Typography>
            <Box sx={{ 
              display: 'flex', 
              gap: 2, 
              overflowX: 'auto',
              pb: 2
            }}>
              {[1, 2, 3].map((i) => (
                <Card 
                  key={i}
                  className="book-card"
                  sx={{ 
                    minWidth: 120,
                    maxWidth: 120,
                    cursor: 'pointer'
                  }}
                >
                  <CardMedia
                    component="img"
                    height="160"
                    image={`https://images.unsplash.com/photo-${1500000000000 + i}?w=120&h=160&fit=crop`}
                    alt="Book cover"
                    sx={{ objectFit: 'cover' }}
                  />
                </Card>
              ))}
            </Box>
          </Grid>

          {/* Favorites Shelf */}
          <Grid item xs={12} md={6} component="div">
            <Typography 
              variant="h3" 
              sx={{ 
                fontFamily: 'var(--font-heading)',
                color: 'var(--text-primary)',
                fontWeight: 600,
                mb: 3
              }}
            >
              Favorites
            </Typography>
            <Box sx={{ 
              display: 'flex', 
              gap: 2, 
              overflowX: 'auto',
              pb: 2
            }}>
              {[1, 2].map((i) => (
                <Card 
                  key={i}
                  className="book-card"
                  sx={{ 
                    minWidth: 120,
                    maxWidth: 120,
                    cursor: 'pointer'
                  }}
                >
                  <CardMedia
                    component="img"
                    height="160"
                    image={`https://images.unsplash.com/photo-${1600000000000 + i}?w=120&h=160&fit=crop`}
                    alt="Book cover"
                    sx={{ objectFit: 'cover' }}
                  />
                </Card>
              ))}
            </Box>
          </Grid>
        </Grid>

        {/* All Books Section */}
        {searchQuery && (
          <Box sx={{ mb: 4 }}>
            <Typography 
              variant="h3" 
              sx={{ 
                fontFamily: 'var(--font-heading)',
                color: 'var(--text-primary)',
                fontWeight: 600,
                mb: 3
              }}
            >
              Search Results
            </Typography>
          </Box>
        )}

        {loading && (
          <Box sx={{ display: 'flex', justifyContent: 'center', py: 4 }}>
            <CircularProgress sx={{ color: 'var(--accent-orange)' }} />
          </Box>
        )}

        {error && (
          <Alert severity="error" sx={{ mb: 3 }}>
            {error.message || String(error)}
          </Alert>
        )}

        {data?.content && data.content.length > 0 && (
          <Box>
            <Typography 
              variant="h3" 
              sx={{ 
                fontFamily: 'var(--font-heading)',
                color: 'var(--text-primary)',
                fontWeight: 600,
                mb: 3
              }}
            >
              All Books
            </Typography>

            <Grid container spacing={3}>
              {data.content.map(book => (
                <Grid item xs={12} sm={6} md={4} lg={3} key={book.id} component="div">
                  <Card 
                    className="book-card"
                    sx={{ 
                      height: '100%',
                      cursor: 'pointer',
                      '&:hover': {
                        transform: 'translateY(-8px)',
                        boxShadow: 'var(--shadow-medium)'
                      }
                    }}
                    onClick={() => navigate(`/books/${book.id}`)}
                  >
                    {book.cover ? (
                      <CardMedia
                        component="img"
                        height="200"
                        image={typeof book.cover === 'string' && book.cover.startsWith('http') ? 
                               `http://localhost:8088/api/v1/books/cover/${book.id}` : 
                               `data:image/jpeg;base64,${book.cover}`}
                        alt={book.title}
                        sx={{ objectFit: 'cover' }}
                      />
                    ) : (
                      <CardMedia
                        component="div"
                        sx={{ 
                          height: 200, 
                          background: 'var(--background-secondary)', 
                          display: 'flex', 
                          alignItems: 'center', 
                          justifyContent: 'center' 
                        }}
                      >
                        <Typography 
                          variant="body2" 
                          sx={{ 
                            color: 'var(--text-muted)',
                            fontFamily: 'var(--font-body)'
                          }}
                        >
                          No Cover
                        </Typography>
                      </CardMedia>
                    )}
                    <CardContent sx={{ p: 2 }}>
                      <Typography 
                        variant="subtitle1" 
                        sx={{ 
                          fontWeight: 600,
                          fontFamily: 'var(--font-heading)',
                          color: 'var(--text-primary)',
                          mb: 0.5,
                          lineHeight: 1.3
                        }}
                      >
                        {book.title}
                      </Typography>
                      <Typography 
                        variant="body2" 
                        sx={{ 
                          color: 'var(--text-secondary)',
                          mb: 1,
                          fontFamily: 'var(--font-body)'
                        }}
                      >
                        by {book.authorName}
                      </Typography>
                      <Button 
                        variant="outlined" 
                        size="small" 
                        fullWidth
                        onClick={(e) => {
                          e.stopPropagation();
                          navigate(`/books/${book.id}`);
                        }}
                        sx={{
                          borderColor: 'var(--border-color)',
                          color: 'var(--text-primary)',
                          fontFamily: 'var(--font-body)',
                          '&:hover': {
                            borderColor: 'var(--accent-orange)',
                            color: 'var(--accent-orange)'
                          }
                        }}
                      >
                        View Details
                      </Button>
                    </CardContent>
                  </Card>
                </Grid>
              ))}
            </Grid>

            {data && (
              <Box sx={{ mt: 4 }}>
                <PaginationControls
                  currentPage={page}
                  totalPages={data.totalPages || 0}
                  pageSize={size}
                  totalElements={data.totalElements || 0}
                  onPageChange={setPage}
                  onPageSizeChange={setSize}
                  loading={loading}
                />
              </Box>
            )}
          </Box>
        )}
      </Container>
    </Box>
  );
};

export default BookListPage;
