import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Typography,
  Box,
  Card,
  CardContent,
  CardMedia,
  Button,
  CircularProgress,
  Alert,
  Grid,
  Avatar,
  LinearProgress,
  IconButton
} from '@mui/material';
import {
  FavoriteBorder,
  Star,
  TrendingUp,
  Coffee,
  Lightbulb
} from '@mui/icons-material';
import PaginationControls from '../components/PaginationControls';
import { useBooks } from '../hooks/useBooks';

// Mock currently reading data - in a real app, this would come from an API
const currentlyReading = {
  title: "Harry Potter and the Sorcerer's Stone",
  author: "J.K. Rowling",
  progress: 65,
  cover: "https://images.unsplash.com/photo-1603871165848-0aa92c869fa1?w=100&h=150&fit=crop"
};

// Mock trending books data - in a real app, this would come from an API
const trendingBooks = [
  {
    id: 1,
    title: "Harry Potter and the Sorcerer's Stone",
    author: "J.K. Rowling",
    rating: 4.5,
    cover: "https://images.unsplash.com/photo-1603871165848-0aa92c869fa1?w=110&h=170&fit=crop"
  },
  {
    id: 2,
    title: "It Ends with Us",
    author: "Colleen Hoover",
    rating: 4.3,
    cover: "https://images.unsplash.com/photo-1544947950-fa07a98d237f?w=110&h=170&fit=crop"
  },
  {
    id: 3,
    title: "The Midnight Library",
    author: "Matt Haig",
    rating: 4.3,
    cover: "https://images.unsplash.com/photo-1481627834876-b7833e8f5570?w=110&h=170&fit=crop"
  },
  {
    id: 4,
    title: "Circe",
    author: "Madeline Miller",
    rating: 4.4,
    cover: "https://images.unsplash.com/photo-1512820790803-83ca734da794?w=110&h=170&fit=crop"
  },
  {
    id: 5,
    title: "The Seven Husbands of Evelyn Hugo",
    author: "Taylor Jenkins Reid",
    rating: 4.2,
    cover: "https://images.unsplash.com/photo-1543002588-bfa74002ed7e?w=110&h=170&fit=crop"
  }
];

// Mock friends feed data
const friendsFeed = [
  {
    id: 1,
    name: "Emma Robinson",
    avatar: "E",
    bookTitle: "The Silent Patient",
    rating: 4.5,
    review: "An engaging, fast-paced mystery with intriguing characters and lots of twists. I really enjoyed it!",
    likes: 15,
    comments: 4
  }
];

// Mock to-read and favorites data
const toReadBooks = [
  { id: 1, cover: "https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=90&h=135&fit=crop" },
  { id: 2, cover: "https://images.unsplash.com/photo-1544947950-fa07a98d237f?w=90&h=135&fit=crop" },
  { id: 3, cover: "https://images.unsplash.com/photo-1481627834876-b7833e8f5570?w=90&h=135&fit=crop" },
  { id: 4, cover: "https://images.unsplash.com/photo-1512820790803-83ca734da794?w=90&h=135&fit=crop" }
];

const favoriteBooks = [
  { id: 1, cover: "https://images.unsplash.com/photo-1603871165848-0aa92c869fa1?w=90&h=135&fit=crop" },
  { id: 2, cover: "https://images.unsplash.com/photo-1544947950-fa07a98d237f?w=90&h=135&fit=crop" }
];

const BookListPage: React.FC = () => {
  const { data, loading, error, page, setPage, size, setSize } = useBooks();
  const navigate = useNavigate();
  const [searchQuery] = useState('');

  const renderStars = (rating: number) => {
    return Array.from({ length: 5 }, (_, i) => (
      <Star 
        key={i} 
        sx={{ 
          color: i < rating ? '#FFD700' : '#E0E0E0',
          fontSize: '0.75rem'
        }} 
      />
    ));
  };

  return (
    <Box sx={{ 
      background: 'linear-gradient(135deg, #FDF5E6 0%, #F4E3C1 100%)',
      minHeight: '100vh',
      py: 3, // 24px padding
      px: 3,
      position: 'relative',
      '&::before': {
        content: '""',
        position: 'absolute',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        background: 'url("data:image/svg+xml,%3Csvg width="100" height="100" xmlns="http://www.w3.org/2000/svg"%3E%3Cdefs%3E%3Cpattern id="bookshelf" x="0" y="0" width="100" height="100" patternUnits="userSpaceOnUse"%3E%3Crect width="100" height="100" fill="none"/%3E%3Crect x="10" y="20" width="80" height="4" fill="%23E6D7C3" opacity="0.1"/%3E%3Crect x="10" y="40" width="80" height="4" fill="%23E6D7C3" opacity="0.1"/%3E%3Crect x="10" y="60" width="80" height="4" fill="%23E6D7C3" opacity="0.1"/%3E%3C/pattern%3E%3C/defs%3E%3Crect width="100%" height="100%" fill="url(%23bookshelf)"/%3E%3C/svg%3E")',
        opacity: 0.3,
        pointerEvents: 'none'
      }
    }}>
      <Container maxWidth="xl" sx={{ position: 'relative', zIndex: 1 }}>
        {/* 3-Column Layout */}
        <Grid container spacing={4} sx={{ mb: 4 }}>

          {/* LEFT PANEL — Currently Reading */}
          <Grid item xs={12} md={4} component="div">
            <Typography 
              variant="h3" 
              sx={{ 
                fontFamily: 'Playfair Display, serif',
                color: '#4B3F30',
                fontWeight: 600,
                mb: 3,
                fontSize: '1.5rem'
              }}
            >
              Currently Reading
            </Typography>

            {currentlyReading ? (
              <Card sx={{ 
                p: 2,
                borderRadius: '10px',
                boxShadow: '0 2px 8px rgba(75, 63, 48, 0.1)',
                border: '1px solid #E6D7C3',
                position: 'relative'
              }}>
                <Box sx={{ display: 'flex', alignItems: 'flex-start', mb: 2 }}>
                  <Coffee sx={{ 
                    color: '#6A5E4D', 
                    fontSize: '1.2rem', 
                    mr: 1, 
                    mt: 0.5 
                  }} />
                  <Box sx={{ flex: 1 }}>
                    <CardMedia
                      component="img"
                      sx={{ 
                        width: '100px', 
                        height: '150px', 
                        objectFit: 'cover',
                        borderRadius: '6px',
                        mb: 2
                      }}
                      image={currentlyReading.cover}
                      alt={currentlyReading.title}
                    />
                    <Typography 
                      variant="h6" 
                      sx={{ 
                        fontFamily: 'Playfair Display, serif',
                        color: '#4B3F30',
                        fontWeight: 600,
                        mb: 0.5,
                        fontSize: '1rem',
                        lineHeight: 1.3
                      }}
                    >
                      {currentlyReading.title}
                    </Typography>
                    <Typography 
                      variant="body2" 
                      sx={{ 
                        color: '#6A5E4D',
                        mb: 2,
                        fontFamily: 'Inter, sans-serif'
                      }}
                    >
                      by {currentlyReading.author}
                    </Typography>

                    {/* Progress Bar */}
                    <Box sx={{ mb: 2 }}>
                      <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                        <Typography 
                          variant="caption" 
                          sx={{ 
                            color: '#6A5E4D',
                            fontFamily: 'Inter, sans-serif'
                          }}
                        >
                          Progress
                        </Typography>
                        <Typography 
                          variant="caption" 
                          sx={{ 
                            color: '#4B3F30',
                            fontFamily: 'Inter, sans-serif',
                            fontWeight: 500
                          }}
                        >
                          {currentlyReading.progress}%
                        </Typography>
                      </Box>
                      <LinearProgress 
                        variant="determinate" 
                        value={currentlyReading.progress}
                        sx={{
                          height: 6,
                          borderRadius: 3,
                          backgroundColor: '#F4E3C1',
                          '& .MuiLinearProgress-bar': {
                            backgroundColor: '#D2691E',
                            borderRadius: 3
                          }
                        }}
                      />
                    </Box>

                    <Button 
                      variant="contained"
                      size="small"
                      sx={{
                        backgroundColor: '#D2691E',
                        color: 'white',
                        fontFamily: 'Inter, sans-serif',
                        fontWeight: 500,
                        fontSize: '0.875rem',
                        '&:hover': {
                          backgroundColor: '#B85A1A'
                        }
                      }}
                    >
                      Update Progress
                    </Button>
                  </Box>
                </Box>
              </Card>
            ) : (
              <Card sx={{ 
                p: 3,
                borderRadius: '10px',
                boxShadow: '0 2px 8px rgba(75, 63, 48, 0.1)',
                border: '1px solid #E6D7C3',
                textAlign: 'center'
              }}>
                <Typography 
                  variant="body1" 
                  sx={{ 
                    color: '#6A5E4D',
                    fontFamily: 'Inter, sans-serif'
                  }}
                >
                  Add your first book!
                </Typography>
              </Card>
            )}
          </Grid>

          {/* CENTER PANEL — Trending Today + From Friends */}
          <Grid item xs={12} md={4} component="div">
            {/* Trending Today Section */}
            <Box sx={{ mb: 4 }}>
              <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                <TrendingUp sx={{ color: '#D2691E', mr: 1, fontSize: '1.5rem' }} />
                <Typography 
                  variant="h3" 
                  sx={{ 
                    fontFamily: 'Playfair Display, serif',
                    color: '#4B3F30',
                    fontWeight: 600,
                    fontSize: '1.5rem'
                  }}
                >
                  Trending Today
                </Typography>
              </Box>

              <Box sx={{ 
                display: 'flex', 
                gap: 2, 
                overflowX: 'auto',
                pb: 1,
                '&::-webkit-scrollbar': {
                  height: 6,
                },
                '&::-webkit-scrollbar-track': {
                  backgroundColor: '#F4E3C1',
                  borderRadius: 3,
                },
                '&::-webkit-scrollbar-thumb': {
                  backgroundColor: '#E6D7C3',
                  borderRadius: 3,
                }
              }}>
                {trendingBooks.map((book) => (
                  <Card 
                    key={book.id}
                    sx={{ 
                      minWidth: '110px',
                      maxWidth: '110px',
                      cursor: 'pointer',
                      borderRadius: '10px',
                      boxShadow: '0 2px 8px rgba(75, 63, 48, 0.1)',
                      border: '1px solid #E6D7C3',
                      '&:hover': {
                        transform: 'translateY(-4px)',
                        boxShadow: '0 4px 16px rgba(75, 63, 48, 0.15)'
                      },
                      transition: 'all 0.3s ease'
                    }}
                    onClick={() => navigate(`/books/${book.id}`)}
                  >
                    <CardMedia
                      component="img"
                      height="170"
                      image={book.cover}
                      alt={book.title}
                      sx={{ objectFit: 'cover' }}
                    />
                    <CardContent sx={{ p: 1.5 }}>
                      <Typography 
                        variant="body2" 
                        sx={{ 
                          fontWeight: 600,
                          fontFamily: 'Playfair Display, serif',
                          color: '#4B3F30',
                          mb: 0.5,
                          lineHeight: 1.2,
                          fontSize: '0.75rem',
                          overflow: 'hidden',
                          textOverflow: 'ellipsis',
                          whiteSpace: 'nowrap'
                        }}
                      >
                        {book.title}
                      </Typography>
                      <Typography 
                        variant="caption" 
                        sx={{ 
                          color: '#6A5E4D',
                          mb: 1,
                          fontFamily: 'Inter, sans-serif',
                          fontSize: '0.7rem'
                        }}
                      >
                        by {book.author}
                      </Typography>
                      {book.rating && (
                        <Box sx={{ display: 'flex', alignItems: 'center' }}>
                          {renderStars(book.rating)}
                          <Typography 
                            variant="caption" 
                            sx={{ 
                              ml: 0.5,
                              color: '#6A5E4D',
                              fontFamily: 'Inter, sans-serif',
                              fontSize: '0.7rem'
                            }}
                          >
                            {book.rating}
                          </Typography>
                        </Box>
                      )}
                    </CardContent>
                  </Card>
                ))}
              </Box>
            </Box>

            {/* From Friends Section */}
            <Box>
              <Typography 
                variant="h3" 
                sx={{ 
                  fontFamily: 'Playfair Display, serif',
                  color: '#4B3F30',
                  fontWeight: 600,
                  mb: 2,
                  fontSize: '1.5rem'
                }}
              >
                From Friends
              </Typography>

              {friendsFeed.length > 0 ? (
                friendsFeed.map((post) => (
                  <Card key={post.id} sx={{ 
                    p: 2,
                    mb: 2,
                    borderRadius: '10px',
                    boxShadow: '0 2px 8px rgba(75, 63, 48, 0.1)',
                    border: '1px solid #E6D7C3'
                  }}>
                    <Box sx={{ display: 'flex', alignItems: 'flex-start', mb: 2 }}>
                      <Avatar 
                        sx={{ 
                          width: 40,
                          height: 40,
                          bgcolor: '#D2691E',
                          mr: 2,
                          fontFamily: 'Inter, sans-serif',
                          fontWeight: 500,
                          fontSize: '0.875rem'
                        }}
                      >
                        {post.avatar}
                      </Avatar>
                      <Box sx={{ flex: 1 }}>
                        <Typography 
                          variant="subtitle2" 
                          sx={{ 
                            fontWeight: 600,
                            color: '#4B3F30',
                            fontFamily: 'Inter, sans-serif',
                            mb: 0.5
                          }}
                        >
                          {post.name}
                        </Typography>
                        <Typography 
                          variant="body2" 
                          sx={{ 
                            color: '#6A5E4D',
                            fontFamily: 'Inter, sans-serif',
                            mb: 1
                          }}
                        >
                          {post.bookTitle} • {post.rating}★
                        </Typography>
                        <Typography 
                          variant="body2" 
                          sx={{ 
                            color: '#3A2C1C',
                            fontFamily: 'Inter, sans-serif',
                            lineHeight: 1.5,
                            mb: 2
                          }}
                        >
                          {post.review}
                        </Typography>

                        <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                          <IconButton size="small" sx={{ color: '#6A5E4D' }}>
                            <FavoriteBorder sx={{ fontSize: '1rem' }} />
                          </IconButton>
                          <Typography 
                            variant="caption" 
                            sx={{ 
                              color: '#6A5E4D',
                              fontFamily: 'Inter, sans-serif'
                            }}
                          >
                            {post.likes}
                          </Typography>
                          <Typography 
                            variant="caption" 
                            sx={{ 
                              color: '#6A5E4D',
                              fontFamily: 'Inter, sans-serif'
                            }}
                          >
                            💬 {post.comments}
                          </Typography>
                        </Box>
                      </Box>
                    </Box>
                  </Card>
                ))
              ) : (
                <Card sx={{ 
                  p: 3,
                  borderRadius: '10px',
                  boxShadow: '0 2px 8px rgba(75, 63, 48, 0.1)',
                  border: '1px solid #E6D7C3',
                  textAlign: 'center'
                }}>
                  <Typography 
                    variant="body1" 
                    sx={{ 
                      color: '#6A5E4D',
                      fontFamily: 'Inter, sans-serif',
                      mb: 1
                    }}
                  >
                    Looking for friends?
                  </Typography>
                  <Typography 
                    variant="body2" 
                    sx={{ 
                      color: '#6A5E4D',
                      fontFamily: 'Inter, sans-serif'
                    }}
                  >
                    Join a book club to see reviews from other readers!
                  </Typography>
                </Card>
              )}
            </Box>
          </Grid>

          {/* RIGHT PANEL — To Read & Favorites */}
          <Grid item xs={12} md={4} component="div">
            {/* To Read Section */}
            <Box sx={{ mb: 4 }}>
              <Typography 
                variant="h3" 
                sx={{ 
                  fontFamily: 'Playfair Display, serif',
                  color: '#4B3F30',
                  fontWeight: 600,
                  mb: 2,
                  fontSize: '1.5rem'
                }}
              >
                To Read
              </Typography>
              <Box sx={{ 
                display: 'grid', 
                gridTemplateColumns: 'repeat(2, 1fr)',
                gap: 1
              }}>
                {toReadBooks.map((book) => (
                  <Card 
                    key={book.id}
                    sx={{ 
                      cursor: 'pointer',
                      borderRadius: '10px',
                      boxShadow: '0 2px 8px rgba(75, 63, 48, 0.1)',
                      border: '1px solid #E6D7C3',
                      '&:hover': {
                        transform: 'translateY(-2px)',
                        boxShadow: '0 4px 16px rgba(75, 63, 48, 0.15)'
                      },
                      transition: 'all 0.3s ease'
                    }}
                  >
                    <CardMedia
                      component="img"
                      sx={{ 
                        width: '100%',
                        height: '135px',
                        objectFit: 'cover'
                      }}
                      image={book.cover}
                      alt="Book cover"
                    />
                  </Card>
                ))}
              </Box>
            </Box>

            {/* Favorites Section */}
            <Box>
              <Typography 
                variant="h3" 
                sx={{ 
                  fontFamily: 'Playfair Display, serif',
                  color: '#4B3F30',
                  fontWeight: 600,
                  mb: 2,
                  fontSize: '1.5rem'
                }}
              >
                Favorites
              </Typography>
              <Box sx={{ 
                display: 'grid', 
                gridTemplateColumns: 'repeat(2, 1fr)',
                gap: 1
              }}>
                {favoriteBooks.map((book) => (
                  <Card 
                    key={book.id}
                    sx={{ 
                      cursor: 'pointer',
                      borderRadius: '10px',
                      boxShadow: '0 2px 8px rgba(75, 63, 48, 0.1)',
                      border: '1px solid #E6D7C3',
                      '&:hover': {
                        transform: 'translateY(-2px)',
                        boxShadow: '0 4px 16px rgba(75, 63, 48, 0.15)'
                      },
                      transition: 'all 0.3s ease'
                    }}
                  >
                    <CardMedia
                      component="img"
                      sx={{ 
                        width: '100%',
                        height: '135px',
                        objectFit: 'cover'
                      }}
                      image={book.cover}
                      alt="Book cover"
                    />
                  </Card>
                ))}
              </Box>
            </Box>
          </Grid>
        </Grid>

        {/* All Books Section */}
        {searchQuery && (
          <Box sx={{ mb: 4 }}>
            <Typography 
              variant="h3" 
              sx={{ 
                fontFamily: 'Playfair Display, serif',
                color: '#4B3F30',
                fontWeight: 600,
                mb: 3,
                fontSize: '1.5rem'
              }}
            >
              Search Results
            </Typography>
          </Box>
        )}

        {loading && (
          <Box sx={{ display: 'flex', justifyContent: 'center', py: 4 }}>
            <CircularProgress sx={{ color: '#D2691E' }} />
          </Box>
        )}

        {error && (
          <Alert severity="error" sx={{ mb: 3 }}>
            {error.message || String(error)}
          </Alert>
        )}

        {data?.content && data.content.length > 0 && (
          <Box>
            <Typography 
              variant="h3" 
              sx={{ 
                fontFamily: 'Playfair Display, serif',
                color: '#4B3F30',
                fontWeight: 600,
                mb: 3,
                fontSize: '1.5rem'
              }}
            >
              All Books
            </Typography>

            <Grid container spacing={3}>
              {data.content.map(book => (
                <Grid item xs={12} sm={6} md={4} lg={3} key={book.id}>
                  <Card 
                    sx={{ 
                      height: '100%',
                      cursor: 'pointer',
                      borderRadius: '10px',
                      boxShadow: '0 2px 8px rgba(75, 63, 48, 0.1)',
                      border: '1px solid #E6D7C3',
                      '&:hover': {
                        transform: 'translateY(-4px)',
                        boxShadow: '0 4px 16px rgba(75, 63, 48, 0.15)'
                      },
                      transition: 'all 0.3s ease'
                    }}
                    onClick={() => navigate(`/books/${book.id}`)}
                  >
                    {book.cover ? (
                      <CardMedia
                        component="img"
                        height="200"
                        image={typeof book.cover === 'string' && book.cover.startsWith('http') ? 
                               `http://localhost:8088/api/v1/books/cover/${book.id}` : 
                               `data:image/jpeg;base64,${book.cover}`}
                        alt={book.title}
                        sx={{ objectFit: 'cover' }}
                      />
                    ) : (
                      <CardMedia
                        component="div"
                        sx={{ 
                          height: 200, 
                          background: '#F4E3C1', 
                          display: 'flex', 
                          alignItems: 'center', 
                          justifyContent: 'center' 
                        }}
                      >
                        <Typography 
                          variant="body2" 
                          sx={{ 
                            color: '#6A5E4D',
                            fontFamily: 'Inter, sans-serif'
                          }}
                        >
                          No Cover
                        </Typography>
                      </CardMedia>
                    )}
                    <CardContent sx={{ p: 2 }}>
                      <Typography 
                        variant="subtitle1" 
                        sx={{ 
                          fontWeight: 600,
                          fontFamily: 'Playfair Display, serif',
                          color: '#4B3F30',
                          mb: 0.5,
                          lineHeight: 1.3
                        }}
                      >
                        {book.title}
                      </Typography>
                      <Typography 
                        variant="body2" 
                        sx={{ 
                          color: '#6A5E4D',
                          mb: 1,
                          fontFamily: 'Inter, sans-serif'
                        }}
                      >
                        by {book.authorName}
                      </Typography>
                      <Button 
                        variant="outlined" 
                        size="small" 
                        fullWidth
                        onClick={(e) => {
                          e.stopPropagation();
                          navigate(`/books/${book.id}`);
                        }}
                        sx={{
                          borderColor: '#E6D7C3',
                          color: '#4B3F30',
                          fontFamily: 'Inter, sans-serif',
                          '&:hover': {
                            borderColor: '#D2691E',
                            color: '#D2691E'
                          }
                        }}
                      >
                        View Details
                      </Button>
                    </CardContent>
                  </Card>
                </Grid>
              ))}
            </Grid>

            {data && (
              <Box sx={{ mt: 4 }}>
                <PaginationControls
                  currentPage={page}
                  totalPages={data.totalPages || 0}
                  pageSize={size}
                  totalElements={data.totalElements || 0}
                  onPageChange={setPage}
                  onPageSizeChange={setSize}
                  loading={loading}
                />
              </Box>
            )}
          </Box>
        )}

        {/* Decorative Lamp */}
        <Box sx={{ 
          position: 'fixed', 
          bottom: 24, 
          right: 24,
          opacity: 0.1,
          pointerEvents: 'none',
          zIndex: 0
        }}>
          <Lightbulb sx={{ fontSize: '4rem', color: '#4B3F30' }} />
        </Box>
      </Container>
    </Box>
  );
};

export default BookListPage;
