import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import {
  AppBar,
  Toolbar,
  Typography,
  Button,
  IconButton,
  Drawer,
  List,
  ListItem,
  ListItemButton,
  ListItemText,
  Box,
  TextField,
  Stack,
  useMediaQuery,
  useTheme,
  Avatar,
  Divider
} from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import SearchIcon from '@mui/icons-material/Search';
import { tokenService } from '../services/tokenService';

const Navbar: React.FC = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [user, setUser] = useState<any>(null);
  const [search, setSearch] = useState('');
  const [drawerOpen, setDrawerOpen] = useState(false);
  const navigate = useNavigate();
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));

  useEffect(() => {
    const checkAuth = () => {
      const loggedIn = tokenService.isLoggedIn();
      setIsLoggedIn(loggedIn);
      if (loggedIn) {
        setUser(tokenService.getUser());
      }
    };
    checkAuth();
    window.addEventListener('storage', checkAuth);
    return () => window.removeEventListener('storage', checkAuth);
  }, []);

  const handleLogout = () => {
    tokenService.removeToken();
    setIsLoggedIn(false);
    setUser(null);
    navigate('/login');
  };

  const handleSearch = (e: React.FormEvent) => {
    e.preventDefault();
    if (search.trim()) {
      navigate(`/books?search=${encodeURIComponent(search.trim())}`);
    }
  };

  const navLinks = [
    { label: 'Home', to: '/books', show: true },
    { label: 'My Books', to: '/my-books', show: isLoggedIn },
    { label: 'Browse', to: '/books', show: true },
    { label: 'Currently Reading', to: '/borrowed-books', show: isLoggedIn },
    { label: 'TBR', to: '/tbr', show: isLoggedIn },
    { label: 'Read', to: '/read', show: isLoggedIn },
    { label: 'Add to Library', to: '/add-book', show: isLoggedIn },
  ];

  const filteredLinks = navLinks.filter(link => link.show);

  const drawerContent = (
    <Box sx={{ width: 280, p: 2, bgcolor: 'var(--background-primary)' }}>
      <Typography variant="h4" component="div" sx={{ 
        fontFamily: 'var(--font-heading)', 
        color: 'var(--text-primary)', 
        mb: 2,
        fontWeight: 700
      }}>
        BookNexus
      </Typography>
      <Divider sx={{ mb: 2, borderColor: 'var(--border-color)' }} />
      <List>
        {filteredLinks.map(link => (
          <ListItem key={link.label} disablePadding sx={{ mb: 1 }}>
            <ListItemButton 
              component={Link} 
              to={link.to}
              sx={{
                borderRadius: 'var(--radius-sm)',
                '&:hover': {
                  backgroundColor: 'var(--background-secondary)',
                  color: 'var(--accent-orange)'
                }
              }}
            >
              <ListItemText 
                primary={link.label} 
                sx={{ 
                  fontFamily: 'var(--font-body)',
                  color: 'var(--text-primary)'
                }}
              />
            </ListItemButton>
          </ListItem>
        ))}
        {isLoggedIn && (
          <ListItem disablePadding sx={{ mb: 1 }}>
            <ListItemButton 
              onClick={handleLogout}
              sx={{
                borderRadius: 'var(--radius-sm)',
                '&:hover': {
                  backgroundColor: 'var(--background-secondary)',
                  color: 'var(--accent-orange)'
                }
              }}
            >
              <ListItemText 
                primary="Logout" 
                sx={{ 
                  fontFamily: 'var(--font-body)',
                  color: 'var(--text-primary)'
                }}
              />
            </ListItemButton>
          </ListItem>
        )}
      </List>
      {isLoggedIn && (
        <>
          <Divider sx={{ my: 2, borderColor: 'var(--border-color)' }} />
          <Box component="form" onSubmit={handleSearch} sx={{ mb: 2 }}>
            <TextField
              size="small"
              variant="outlined"
              placeholder="Search books..."
              value={search}
              onChange={e => setSearch(e.target.value)}
              fullWidth
              sx={{
                '& .MuiOutlinedInput-root': {
                  backgroundColor: 'var(--card-background)',
                  borderColor: 'var(--border-color)',
                  borderRadius: 'var(--radius-sm)',
                  '& fieldset': {
                    borderColor: 'var(--border-color)',
                  },
                  '&:hover fieldset': {
                    borderColor: 'var(--text-secondary)',
                  },
                  '&.Mui-focused fieldset': {
                    borderColor: 'var(--accent-orange)',
                  },
                },
                '& .MuiInputBase-input': {
                  fontFamily: 'var(--font-body)',
                  color: 'var(--text-primary)',
                }
              }}
            />
          </Box>
          <Typography 
            variant="body1" 
            component={Link} 
            to={`/profile/${user?.id || 1}`}
            sx={{ 
              color: 'var(--text-primary)', 
              whiteSpace: 'nowrap',
              textDecoration: 'none',
              cursor: 'pointer',
              fontFamily: 'var(--font-body)',
              '&:hover': {
                color: 'var(--accent-orange)',
                textDecoration: 'underline'
              }
            }}
          >
            Welcome {user?.name || user?.email || 'User'}
          </Typography>
        </>
      )}
    </Box>
  );

  return (
    <AppBar 
      position="static" 
      sx={{ 
        backgroundColor: 'var(--card-background)',
        boxShadow: 'var(--shadow-light)',
        borderBottom: '1px solid var(--border-color)'
      }}
    >
      <Toolbar sx={{ justifyContent: 'space-between', px: { xs: 2, md: 4 } }}>
        {/* Left: App Name */}
        <Typography 
          variant="h4" 
          component={Link} 
          to="/books" 
          sx={{ 
            color: 'var(--text-primary)', 
            textDecoration: 'none', 
            mr: 4, 
            display: { xs: 'block', md: 'block' },
            fontFamily: 'var(--font-heading)',
            fontWeight: 700
          }}
        >
          BookNexus
        </Typography>

        {isMobile ? (
          <>
            <Box sx={{ flexGrow: 1 }} />
            <IconButton 
              color="inherit" 
              edge="end" 
              onClick={() => setDrawerOpen(true)} 
              sx={{ 
                ml: 'auto',
                color: 'var(--text-primary)',
                '&:hover': {
                  backgroundColor: 'var(--background-secondary)'
                }
              }}
            >
              <MenuIcon />
            </IconButton>
            <Drawer 
              anchor="left" 
              open={drawerOpen} 
              onClose={() => setDrawerOpen(false)}
              PaperProps={{
                sx: {
                  backgroundColor: 'var(--background-primary)',
                  borderRight: '1px solid var(--border-color)'
                }
              }}
            >
              {drawerContent}
            </Drawer>
          </>
        ) : (
          <>
            {/* Center: Search Bar */}
            <Box sx={{ flexGrow: 1, maxWidth: 400, mx: 4 }}>
              <Box component="form" onSubmit={handleSearch}>
                <TextField
                  size="small"
                  variant="outlined"
                  placeholder="Search books..."
                  value={search}
                  onChange={e => setSearch(e.target.value)}
                  fullWidth
                  sx={{
                    '& .MuiOutlinedInput-root': {
                      backgroundColor: 'var(--card-background)',
                      borderColor: 'var(--border-color)',
                      borderRadius: 'var(--radius-sm)',
                      '& fieldset': {
                        borderColor: 'var(--border-color)',
                      },
                      '&:hover fieldset': {
                        borderColor: 'var(--text-secondary)',
                      },
                      '&.Mui-focused fieldset': {
                        borderColor: 'var(--accent-orange)',
                      },
                    },
                    '& .MuiInputBase-input': {
                      fontFamily: 'var(--font-body)',
                      color: 'var(--text-primary)',
                    }
                  }}
                />
              </Box>
            </Box>

            {/* Right: Navigation Links */}
            <Stack direction="row" spacing={2} sx={{ mr: 2 }}>
              <Button 
                color="inherit" 
                component={Link} 
                to="/books"
                sx={{
                  color: 'var(--text-primary)',
                  fontFamily: 'var(--font-body)',
                  fontWeight: 500,
                  '&:hover': {
                    backgroundColor: 'var(--background-secondary)',
                    color: 'var(--accent-orange)'
                  }
                }}
              >
                Home
              </Button>
              {isLoggedIn && (
                <Button 
                  color="inherit" 
                  component={Link} 
                  to="/my-books"
                  sx={{
                    color: 'var(--text-primary)',
                    fontFamily: 'var(--font-body)',
                    fontWeight: 500,
                    '&:hover': {
                      backgroundColor: 'var(--background-secondary)',
                      color: 'var(--accent-orange)'
                    }
                  }}
                >
                  My Books
                </Button>
              )}
              <Button 
                color="inherit" 
                component={Link} 
                to="/books"
                sx={{
                  color: 'var(--text-primary)',
                  fontFamily: 'var(--font-body)',
                  fontWeight: 500,
                  '&:hover': {
                    backgroundColor: 'var(--background-secondary)',
                    color: 'var(--accent-orange)'
                  }
                }}
              >
                Browse
              </Button>
            </Stack>

            {/* Right: User Avatar/Login */}
            {isLoggedIn ? (
              <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                <Typography 
                  variant="body1" 
                  component={Link} 
                  to={`/profile/${user?.id || 1}`}
                  sx={{ 
                    color: 'var(--text-primary)', 
                    whiteSpace: 'nowrap',
                    textDecoration: 'none',
                    cursor: 'pointer',
                    fontFamily: 'var(--font-body)',
                    fontWeight: 500,
                    '&:hover': {
                      color: 'var(--accent-orange)',
                      textDecoration: 'underline'
                    }
                  }}
                >
                  {user?.name || user?.email || 'User'}
                </Typography>
                <Avatar 
                  sx={{ 
                    width: 32, 
                    height: 32,
                    bgcolor: 'var(--accent-orange)',
                    fontFamily: 'var(--font-body)',
                    fontSize: '0.875rem'
                  }}
                >
                  {(user?.name || user?.email || 'U').charAt(0).toUpperCase()}
                </Avatar>
              </Box>
            ) : (
              <Stack direction="row" spacing={1}>
                <Button 
                  variant="outlined"
                  component={Link} 
                  to="/login"
                  sx={{
                    color: 'var(--text-primary)',
                    borderColor: 'var(--border-color)',
                    fontFamily: 'var(--font-body)',
                    fontWeight: 500,
                    '&:hover': {
                      backgroundColor: 'var(--background-secondary)',
                      borderColor: 'var(--text-secondary)'
                    }
                  }}
                >
                  Login
                </Button>
                <Button 
                  variant="contained"
                  component={Link} 
                  to="/register"
                  sx={{
                    backgroundColor: 'var(--accent-orange)',
                    color: 'white',
                    fontFamily: 'var(--font-body)',
                    fontWeight: 500,
                    '&:hover': {
                      backgroundColor: '#B85A1A'
                    }
                  }}
                >
                  Register
                </Button>
              </Stack>
            )}
          </>
        )}
      </Toolbar>
    </AppBar>
  );
};

export default Navbar;
